void process_checktags(char *mterm, int this_path, char *this_dui, char *textloc, char *trigger)
{
   int done, found;
   int i, j;
   long lindex;
   TCHECKTAG *new;

   if(RTM_Debug)
   {
       fprintf(fout, "process_checktags(%s)\n", mterm);
       fflush(fout);
   } /* fi */

   lindex = -1;
   found = FALSE;
   if(ct_table)
   {
       for(i = 0; !found && (i < ct_count); i++)
       {
          if (strcmp(this_dui, ct_table[i].dui) == 0)
          {
             if(RTM_Debug)
             {
                 fprintf(fout, "process_checktags: %s -- FOUND)\n", mterm);
                 fflush(fout);
             } /* fi */

             found = TRUE;
             ct_table[i].paths[this_path] = TRUE;
             if(strlen(textloc) > 0)
             {
                 if(strstr(textloc, ct_table[i].textloc) != NULL)
                   strcpy(ct_table[i].textloc, textloc);

                 else if(strstr(ct_table[i].textloc, textloc) == NULL)
                 {
                     if(strcmp(ct_table[i].textloc, "") != 0)
                       strcat(ct_table[i].textloc, ";");
 
                     strcat(ct_table[i].textloc, textloc);
                 } /* else fi */
             } /* fi */

             if(strstr(ct_table[i].trigger, trigger) == NULL)
             {
                 if(strlen(ct_table[i].trigger) > 0)
                   strcat(ct_table[i].trigger, ";");

                 strcat(ct_table[i].trigger, trigger);
             } /* fi */
          } /* fi */
       } /* for */
   } /* fi ct_table */

   if(!found) /* Check mt_table */
   {
       lindex = search_index(this_dui);
       if(lindex == -1)  /* Not found, look for MH & ET */
           lindex = search_indexMH(mterm);

       if(RTM_Debug)
       {
           fprintf(fout, "process_checktags: %s -- !FOUND - %ld mt_table?)\n",
                   mterm, lindex);
           fflush(fout);
       } /* fi */

       if(lindex > -1)
       {
           if(RTM_Debug)
           {
               fprintf(fout, "process_checktags: %s -- Existing mt_table\n",
                       mterm);
               fflush(fout);
           } /* fi */

           if(strstr(mt_table[lindex].trigger, trigger) == NULL)
           {
               if(strlen(mt_table[lindex].trigger) > 0)
                 strcat(mt_table[lindex].trigger, ";");

               strcat(mt_table[lindex].trigger, trigger);

               /* JGM Added 9/4/2013 - if process_checkTag, force display */

               mt_table[lindex].paths[this_path] = TRUE;
               mt_table[lindex].safe = TRUE;
               mt_table[lindex].oktoprint = TRUE;
               mt_table[lindex].forcePrint = TRUE;
               mt_table[lindex].datatype = CT;
           } /* fi */
       } /* fi */

       else /* not found, so add */
       {
           if(RTM_Debug)
           {
               fprintf(fout, "process_checktags: %s -- ADD %ld\n",
                       mterm, ct_count);
               fflush(fout);
           } /* fi */

           if(ct_table)
	     new = (TCHECKTAG *)realloc(ct_table, 
                                        (ct_count + 1) * TCHECKTAG_SIZE);
           else
	     new = (TCHECKTAG *)calloc(1, TCHECKTAG_SIZE);

           if(new == (TCHECKTAG *)NULL)
             return;
    
           ct_table = new;
           strcpy(ct_table[ct_count].mh, mterm);
           strcpy(ct_table[ct_count].dui, this_dui);
           strcpy(ct_table[ct_count].textloc, textloc);
           strcpy(ct_table[ct_count].trigger, trigger);

           for(j = 0; j < NUM_PATHS; j++)
             ct_table[ct_count].paths[j] = FALSE;
           ct_table[ct_count].paths[this_path] = TRUE;

           ct_table[ct_count].oktoprint = TRUE;
           ct_table[ct_count].hasPrinted = FALSE;
           ct_count++;
       } /* else */
   } /* fi */
    
   new = NULL;
} /* process_checktags */



