int checkD01_D25_and_E05_and_H01()
{
   int i, k;
   int rtn = FALSE;

   for(k = 0; !rtn && (k < max); k++)
   {
      if(mt_table[k].safe && (mt_table[k].num_treecodes > 0))
      {
         for(i = 0; !rtn && (i < mt_table[k].num_treecodes); i++) 
         {
             /* We are looking for D01 - D25 -- so if it starts with a "D"
                and isn't within D26 or D27, we're all set.
             */

             if(mt_table[k].treecodes[i][0] == 'D')
             {
                if((strncmp(mt_table[k].treecodes[i], "D26", 3) != 0) &&
                           (strncmp(mt_table[k].treecodes[i], "D27", 3) != 0))
                {
                   rtn = TRUE;
                   trigger_pos = k;
                   trigger_code = i;
                } /* fi */
             } /* fi */

             /* Investigative Techniques E05 */

             if((mt_table[k].treecodes[i][0] == 'E') || 
                          (mt_table[k].treecodes[i][0] == 'H'))
             {
                if((strncmp(mt_table[k].treecodes[i], "E05", 3) == 0) ||
                   (strncmp(mt_table[k].treecodes[i], "H01.181.341", 11) == 0))
                {
                   rtn = TRUE;
                   trigger_pos = k;
                   trigger_code = i;
                } /* fi */
             } /* fi */
         } /* for */
      } /* fi */
   } /* for */

   return(rtn);
} /* checkD01_D25_and_E05_and_H01 */



