int checkG04_G11treecodes()
{
   int done;
   int i, k, ok;
   int rtn = FALSE;

   done = FALSE;

   /* JGM: Updated 12/22/03 per Susanne's email.  From G04 - G11
           to G04 OR G06-G11 (no G05 now).
   */

   for(k = 0; !rtn && (k < max); k++)
   {
      if(mt_table[k].safe && (mt_table[k].num_treecodes > 0))
      {
         for(i = 0; !done && (i < mt_table[k].num_treecodes); i++) 
         {
            if(mt_table[k].treecodes[i][0] == 'G')
            {
               ok = FALSE;
               if(mt_table[k].treecodes[i][1] == '0')
               {
                  if((mt_table[k].treecodes[i][2] == '4') ||
                     (mt_table[k].treecodes[i][2] == '6') ||
                     (mt_table[k].treecodes[i][2] == '7') ||
                     (mt_table[k].treecodes[i][2] == '8') ||
                     (mt_table[k].treecodes[i][2] == '9'))
                    ok = TRUE;
               } /* fi */

               else if(mt_table[k].treecodes[i][1] == '1')
               {
                  if((mt_table[k].treecodes[i][2] == '0') ||
                     (mt_table[k].treecodes[i][2] == '1'))
                    ok = TRUE;
               } /* else fi */

               if(ok)
               {
                   done = TRUE;
                   rtn = TRUE;
                   trigger_pos = k;
                   trigger_code = i;
               } /* fi */
            } /* fi */
        } /* for */
     } /* fi */
   } /* for */

   return(rtn);
} /* checkG04_G11treecodes */




