void doEntryTerm(int mt_pos)
{
   int Title_field = FALSE;
   int ET_Title_found = FALSE;
   int MH_Title_field = FALSE;
   int MH_found = FALSE;
   int MeSH_Term = FALSE;
   char *tmp, *tmp2, *tmp3, *eterm, *tmp5, *loc, *tmp7, *tmp8, *tmp9, *tmp10;
   char tCUI[25];
   char tDUI[25];
   char **rows;
   int i, k, n, done, found, pos;
   long len, j, dpos, eLen;
   char *found_eterms[20];
   char found_eCUIs[20][25];
   int found_title[20];
   int max_found = 20;
   int num_found = 0;
   int do_sub = FALSE;
   int Debug = FALSE;
   int first_time = TRUE;
   int first_sub = -1;

   /* February 16, 2017:

          "Isopod"-ab-1-"isopod"-adj-0
          "Indometacin"-ab-4-"indomethacin"-noun-0

      Trigger Information -- Comma separated sextuple showing what triggered MMI to identify this UMLS concept. Ordering tends to go from last to first found. The sextuple consists of
          "UMLS concept-loc-locPos-text-Part of Speech-Negation Flag".

      * UMLS Concept (Preferred or Synonym Text)

      * loc -- Location in the text if identifiable. ti - Title, ab - Abstract,
               and tx - Free Text

      * locPos -- Number of the utterance within the loc starting with one (1).
        For example, "ti-1" denotes the first utterance in the title.

      * text -- The actual text mapped to this UMLS concept identification.

      * Part of Speech -- Determined by the MedPost Tagger or Lexical Lookup 

      * Negation Flag -- 1 if the text is considered Negated by MetaMap and
           0 if it is not considered Negated.
*/

   if(showETsD)
     Debug = TRUE;

   eLen = (long)strlen(mt_table[mt_pos].entry_term);
   tmp = (char *)malloc(eLen + 1000);
   tmp2 = (char *)malloc(eLen + 1000);
   tmp3 = (char *)malloc(eLen + 1000);
   eterm = (char *)malloc(eLen + 1000);
   tmp5 = (char *)malloc(eLen + 1000);
   loc = (char *)malloc(eLen + 1000);
   tmp7 = (char *)malloc(eLen + 1000);
   tmp8 = (char *)malloc(eLen + 1000);
   tmp9 = (char *)malloc(eLen + 1000);
   tmp10 = (char *)malloc(eLen + 10000);

   /* Init the found entry terms arrays */

   for(i = 0; i < max_found; i++)
   {
      found_eterms[i] = (char *)malloc(eLen + 1000);
      strcpy(found_eterms[i], "");
      strcpy(found_eCUIs[i], "");
      found_title[i] = FALSE;
   } /* for */

   done = FALSE;
   if(RTM_Debug)
   {
       if(eLen > 0)
       {
          fprintf(fout, "\nOriginal String: %s (%s)\n", mt_table[mt_pos].mh,
             mt_table[mt_pos].dui);
          fflush(fout);
          fprintf(fout, "ET: %s\n", mt_table[mt_pos].entry_term);
          fflush(fout);
       } /* fi */
   } /* fi */

   strcpy(tmp, mt_table[mt_pos].entry_term);
   while(!done && (strlen(tmp) > 0))
   {
      strcpy(tmp2, "");
      strcpy(tmp3, "");
      sscanf(tmp, "%[^:]:%[^\n]", tmp2, tmp3);

      while(!done && (strlen(tmp2) > 0))
      {
         Title_field = FALSE;
         strcpy(eterm, "");
         strcpy(tmp5, "");
         strcpy(loc, "");
         sscanf(tmp2, "%*[^\"]\"%[^\"]\"-%[^,]%[^\n]", eterm, loc, tmp5);

         if(RTM_Debug)
         {
             fprintf(fout, "eterm: #%s#\nloc: #%s#\ntmp5: #%s#\n",
                     eterm, loc, tmp5);
             fflush(fout);
         } /* fi */

         if(strlen(eterm) > 0)
         {
             if(strncmp(loc, "ti-", 3) == 0)
               Title_field = TRUE;

             /* IF we find that we have a MH from the title, we are going
                to recommend that, so there is no sense in continuing. IF
                the MH is not in the title, then we continue, but, mark that
                we did find the MH as a term from MMI.
             */

             if(strcasecmp(eterm, mt_table[mt_pos].mh) == 0)
             {
                   MH_found = TRUE;
                   if(Title_field)
                   {
                      done = TRUE;
                      MH_Title_field = TRUE;
                   } /* fi */
             } /* fi */

             else
             {
                /* We have to convert the term to lowercase since that is
                   what we used for an index into the list of entry terms. */

                if(num_found < max_found)
                {
                   strcpy(tmp7, eterm);
                   len = (long)strlen(tmp7);
                   for(j = 0; j < len; j++)
                      if(isupper(tmp7[j]))
                        tmp7[j] = tolower(tmp7[j]);

                   MeSH_Term = FALSE;
                   query_btree(MHENTRY, tmp7, &rows, &n);
                   if((rows != NULL) && (n > 0))
                   {
                      for(i = 0; i < n; i++)
                      {
                         strcpy(tmp8, "");
                         strcpy(tCUI, "");
                         strcpy(tDUI, "");
                         sscanf(rows[i], 
                                "%[^|]|%[^|]|%[^\n]", tmp8, tCUI, tDUI);

                         if(strcmp(tDUI, mt_table[mt_pos].dui) == 0)
                         {
                            if(hasTUI(tmp8))
                            {
                                MeSH_Term = TRUE;
                                found = FALSE;
                                for(k = 0; !found && (k < num_found); k++)
                                {
                                   if(strcmp(tmp8, found_eterms[k]) == 0)
                                   {
                                      found = TRUE;
                                      if(Title_field)
                                      {
                                         ET_Title_found = TRUE;
                                         found_title[k] = TRUE;
                                      } /* fi */
                                   } /* fi */
                                } /* for */

                                if(!found)
                                {
                                   if(Title_field)
                                      ET_Title_found = TRUE;
                                   found_title[num_found] = Title_field;
                                   strcpy(found_eterms[num_found], tmp8);
                                   strcpy(found_eCUIs[num_found], tDUI);
                                   num_found++;
                                } /* fi */
                            } /* fi hasTUI */
                         } /* fi DUIs */

                         free(rows[i]);
                      } /* for */
                   } /* fi rows */

                   if(rows != NULL)
                     free(rows);
                } /* fi num_found */

                if(Debug)
                {
                   fprintf(fout, "  -- %s (%s) [%s]\n", eterm, 
                           Title_field ? "Title" : "Abstract",
                           MeSH_Term ? "valid Entry Term Match" : 
                           "NOT a valid Entry Term Match");
                   fflush(fout);
                } /* fi */
             } /* else not MH */
         } /* fi */
         strcpy(tmp2, tmp5);
      } /* while */

      if(first_time && (num_found > 0))
      {
         first_time = FALSE;
         first_sub = num_found - 1;
         if(Debug)
         {
            fprintf(fout, "  Identified first_sub: %s:%s (ET Title: %s)\n",
                found_eterms[first_sub], found_eCUIs[first_sub],
                ET_Title_found ? "TRUE" : "FALSE");
            fflush(fout);
         } /* fi */
      } /* fi */

      strcpy(tmp, tmp3);
   } /* while */

   /* So, here is the current situation:
       If we have a legitimate MH (not one from RtM), and
         the MH is in the Title - forget the Entry Terms
         the MH is in the Abstract - then, if the Entry Term is in 
             the Title, we use the Entry Term
             the Abstract, we use the MH.

       If no legitimate MH found, then use the Entry Term
   */

   if(eLen > 0)
   {
      if(Debug)
      {
          fprintf(fout, 
               "  Direct MH term found in article: %s  - in the Title: %s\n", 
               MH_found ? "TRUE" : "FALSE", MH_Title_field ? "TRUE" : "FALSE");
          fflush(fout);
          fprintf(fout, "  Entry Term found in the Title: %s\n", 
                       ET_Title_found ? "TRUE" : "FALSE");
          fflush(fout);
      } /* fi */

      if(MH_Title_field)
      {
         if(Debug)
         {
             fprintf(fout, 
                   "Keeping current term - Rule 1 (Keep Direct MH in Title)\n");
             fflush(fout);
         } /* fi */
      } /* fi */

      else   /* We don't have a Direct MH in the Title */
      {
         do_sub = FALSE;
         if(MH_found)  /* But, we do have a Direct MH in the Abstract */
         {
             if(ET_Title_found)
             {
                 do_sub = TRUE;

                 if(Debug && (num_found > 0))
                 {
                    fprintf(fout, "Replacing current term - Rule 2a (Direct");
                    fflush(fout);
                    fprintf(fout, 
                            " MH found in Abstract, ET found in Title)\n");
                    fflush(fout);
                 } /* fi */

                 else if(Debug)
                 {
                     fprintf(fout, 
             "Do nothing - 2a) Direct MH found, BUT, no alternatives found!\n");
                     fflush(fout);
                 } /* else fi */
             } /* fi */

             else
             {
                if(Debug && (num_found > 0))
                   fprintf(fout, 
                   "Keep term - 2b) Both Direct MH and ET found in Abstract\n");
                 else if(Debug)
                   fprintf(fout, 
             "Do nothing - 2b) Direct MH found, BUT, no alternatives found!\n");
                 fflush(fout);
             } /* else */
         } /* fi */

         else    /* We do not have a Direct MH */
         {

            if(Debug && (num_found > 0))
              fprintf(fout, 
               "Replacing current term - Rule 3 (no Direct MH found)\n");
            else if(Debug)
              fprintf(fout, 
            "Do nothing - 3) no Direct MH found, BUT no alternatives found!\n");
            fflush(fout);

            do_sub = TRUE;
         } /* else */

         /* NOTE: We get the terms in reverse order of appearance in the
                  article from MMI.  So we want to display in the correct
                  order and replace with the first occurrence in the article.
         */

         if(do_sub && (num_found > 0))
         {
            if(first_sub > -1)
              pos = first_sub;
            else
              pos = num_found - 1;

            if(Debug)
            {
               fprintf(fout, 
                      "  -- Replace with: %s - Title: %s\n", found_eterms[pos], 
                      found_title[pos] ? "TRUE" : "FALSE");
               fflush(fout);
            } /* fi */

            sprintf(tmp9, "Entry Term Replacement for \"%s\"\0",
                     mt_table[mt_pos].mh);
            strcpy(mt_table[mt_pos].mh_orig, mt_table[mt_pos].mh);

            /* Search to see if we already have the ET */

            dpos = search_index(found_eCUIs[pos]);
            if((dpos > -1) && mt_table[dpos].oktoprint && (dpos != mt_pos) &&
                mt_table[dpos].paths[MMI])
            {
                mt_table[dpos].ETflag = TRUE;
                mt_table[dpos].score += mt_table[mt_pos].score;

                strcpy(tmp10, mt_table[dpos].trigger);
                strcpy(mt_table[dpos].trigger, tmp9);
                if(strlen(tmp10) > 0)
                {
                   strcat(mt_table[dpos].trigger, ";");
                   strcat(mt_table[dpos].trigger, tmp10);
                } /* fi */

                mt_table[mt_pos].oktoprint = FALSE;
                mt_table[mt_pos].score = 0;
                if(RTM_Debug)
                {
                    fprintf(fout, "Already have ET Removal: #%s#\n",
                             mt_table[mt_pos].mh);
                    fflush(fout);
                } /* fi */
            } /* fi */

            else /* OK, we have a new ET term */
            {
                mt_table[mt_pos].ETflag = TRUE;
                strcpy(tmp10, mt_table[mt_pos].trigger);
                strcpy(mt_table[mt_pos].trigger, tmp9);
                if(strlen(tmp10) > 0)
                {
                   strcat(mt_table[mt_pos].trigger, ";");
                   strcat(mt_table[mt_pos].trigger, tmp10);
                } /* fi */

                strcpy(mt_table[mt_pos].mh, found_eterms[pos]);
                strcpy(mt_table[mt_pos].dui, found_eCUIs[pos]);
            } /* else */

            if((num_found > 1) && Debug)
            {
               for(i = (num_found - 1); i > -1; i--)
               {
                   if(i != pos)
                   {
                      if(Debug)
                      {
                         fprintf(fout, 
                            "  -- Alt. Replacement[%d]: %s - Title: %s\n", i,
                            found_eterms[i], found_title[i] ? "TRUE" : "FALSE");
                          fflush(fout);
                      } /* fi */
                   } /* fi */
               } /* for */
            } /* fi */
         } /* fi */
      } /* else !MH_Title_field */
   } /* fi entry_term */

   if(Debug && (eLen > 0))
      fprintf(fout, "\n");

   free(tmp);
   free(tmp2);
   free(tmp3);
   free(eterm);
   free(tmp5);
   free(loc);
   free(tmp7);
   free(tmp8);
   free(tmp9);
   free(tmp10);

   for(i = 0; i < max_found; i++)
     free(found_eterms[i]);
} /* doEntryTerm */


int trulyFound(char *inSent, char *lookfor, long sLen)
{
   int rtn = FALSE;
   long diff, tlen;
   char *tmp;

   tmp = strstr(inSent, lookfor);
   if(tmp != NULL)
   {
       diff = tmp - inSent;

       /* Check left-side first */

       if((diff == 0) || ((isspace(inSent[diff - 1])) ||
                          (ispunct(inSent[diff - 1]))))
       {
           /* Now check right-side */

           tlen = diff + (int)strlen(lookfor) + 1;
           if((tlen >= sLen) || ((isspace(inSent[tlen - 1])) ||
                          (ispunct(inSent[tlen - 1]))))
             rtn = TRUE;
       } /* fi */
   } /* fi */

   return(rtn);
} /* trulyFound */


void checkForSupChemMods()
{
   long i, j;
   char *foo;
   char tmp[SMALL_LINE + 1];
   int ok, found, first = TRUE;

   for(i = 0; i < max; i++)
   {
      if(mt_table[i].datatype == NM)
      {
         foo = checkSuppMod(mt_table[i].mh);
         if(strcmp(foo, mt_table[i].mh) != 0) /* We have a substitute */
         {
            ok = FALSE;
            if(first)  /* No other NMs above to check, so make change */
              ok = TRUE;

            else /* Check to see if any dups of this term above */
            {
                found = FALSE;
                ok = TRUE;
                for(j = 0; !found && (j < (max + 1)); j++)
                {
                    if((j != i) && (mt_table[j].datatype == NM))
                      if(strcmp(mt_table[j].mh, foo) == 0)
                      {
                          found = TRUE;
                          ok = FALSE;
                          mt_table[i].oktoprint = FALSE;
                          max++;
                          if(RTM_Debug)
                          {
                             fprintf(fout,
                                     "checkForSupChemMods Removal: #%s#\n",
                                     mt_table[i].mh);
                             fflush(fout);
                          } /* fi */
                      } /* fi */
                } /* for */
            } /* else */

            if(ok)
            {
               sprintf(tmp,
                   "SuppChem Update Replacement for \"%s\"\0", mt_table[i].mh);
               strcpy(mt_table[i].mh, foo);
               if(strlen(mt_table[i].trigger) > 0)
                 strcat(mt_table[i].trigger, ";");
               strcat(mt_table[i].trigger, tmp);

               /* If this will be used by anything other than default_DCMS
                  where it's just a list, we need to consider fixing the
                  CUI in mt_table[i].cui to point to the new term.  Problem
                  is that MHCUI doesn't have all terms and nothing else is
                  built to do it.  We may have to look at building a MRCON
                  table then the Trigram method could use it locally versus
                  calling the MRCON server on skr1.
               */
            } /* fi */
         } /* fi mod avail */

         free(foo);
         first = FALSE;
      } /* fi NM term */
   } /* for */
} /* checkForSupChemMods */

