void check_USA_List()
{
   int caseSen, found, isAmbig;
   long i, len, AApos, index;
   char from[MAXLINE + 1], line[MAXLINE + 1], lookFor[SMALL_LINE + 1],
        tmp[SMALL_LINE + 1], ambigTrigger[SMALL_LINE + 1];
   FILE *fp;

    fp = fopen(USA_CaseSenTrigFile, "r");

    if(fp != NULL)
    {
       while(fgets(line, SMALL_LINE, fp) != (char *)NULL)
       {
           if(line[0] != '#')
           {
               found = FALSE;
               caseSen = FALSE;
               strcpy(lookFor, "");
               sscanf(line, "%[^|]|%d", lookFor, &caseSen);
               len = (long)strlen(lookFor);
               if((len > 0) && (caseSen > -1))
               {
                   sprintf(from, "USA Lookup:%s\0", lookFor);

                   if(caseSen)
                   {
                       found = foundInText(lookFor, TRUE, FALSE);

                       /* If found, make sure we don't have plural (SSAs) */

                       if(found)
                       {
                           sprintf(tmp, "%ss\0", lookFor);
                           if(foundInText(lookFor, TRUE, FALSE))
                             found = FALSE;
                       } /* fi */

                       /* See if it is an author defined AA */

                       if(found)
                       {
                           AApos = checkAAs(lookFor);
                           if(AApos > -1)
                             found = FALSE;
                       } /* fi */
                   } /* fi */

                   else
                   {
                       for(i = 0; i < len; i++)
                       {
                           if(isupper(lookFor[i]))
                             lookFor[i] = tolower(lookFor[i]);
                       } /* for */

                       found = foundInText(lookFor, FALSE, FALSE);
                   } /* fi */

                   if(found)
                   {
                       isAmbig = isAmbigTrig(lookFor);
                       if(isAmbig)
                         sprintf(ambigTrigger, "AMBIG/METAPHOR2:%s\0", lookFor);
                       else
                         strcpy(ambigTrigger, "");

                       process_mterm(isAmbig, ambigTrigger, special_list[Special_US].Name,
                               MMI, 1000.0, MH, "", "", from, "D014481", "", "", TRUE);

                       index = search_index("D014481");
                       if(index >= 0)
                         mt_table[index].score += 1000;
                   } /* fi */
               } /* fi */

               else
               {
                   fprintf(stderr, "ERROR: Bad USA Trigger Line: #%s#\n", line);
                   fflush(stderr);
               } /* else */
           } /* fi */
       } /* while */

       fclose(fp);
    } /* fi */

    else
    {
        fprintf(stderr, "ERROR - Unable to open USA Trigger file: #%s#\n",
                USA_CaseSenTrigFile);
        fflush(stderr);
    } /* else */
} /* check_USA_List */


