int verifyPluralVariants(char *entry_term)
{
    int rtn = TRUE;
    long eLen = (long)strlen(entry_term);
    char tmp[eLen + 1], tmpc[eLen + 1], tmp2[eLen + 1],
         word[eLen + 1], rest[eLen + 1], irest[eLen + 1],
         tword[eLen + 1], wword[eLen + 1];
    char *foo = NULL;
    int done;
    long i, diff, pos, len, wLen;

    done = FALSE;
    strcpy(tmpc, entry_term);
    while(!done && (strlen(tmpc) > 0))
    {
        strcpy(tmp, "");
        strcpy(rest, "");
        sscanf(tmpc, "%[^:]:%[^\n]", tmp, rest);

        while(!done && (strlen(tmp) > 0))
        {
            foo = strstr(tmp, "\",\"");
            strcpy(tmp2, "");
            strcpy(irest, "");
            len = (long)strlen(tmp);
            if(foo == NULL)
               diff = len;
            else
               diff = foo - tmp;

            for(i = 0; i < diff; i++)
              tmp2[i] = tmp[i];
            tmp2[diff] = '\0';

            pos = 0;
            for(i = (diff + 1); i < len; i++)
              irest[pos++] = tmp[i];
            irest[pos] = '\0';

            strcpy(word, "");
            strcpy(tword, "");
            if(tmp2[0] == '"')
              sscanf(tmp2, "\"%*[^\"]\"%[^\"]\"%*[^\"]\"%[^\"]",
                     tword, word);
            else
              sscanf(tmp2, "%*[^\"]\"%[^\"]\"%*[^\"]\"%[^\"]",
                     tword, word);

            wLen = (long)strlen(word);
            if(wLen > 0)
            {
                if(wLen > 2)  /* Only want up to 2 char words to review */
                  done = TRUE;
                else
                {
                    sprintf(wword, "%ss\0", word);
                    if(strcasecmp(tword, wword) == 0)
                    {
                        done = TRUE;
                        rtn = FALSE;
                    } /* fi */
                } /* else */
            } /* fi */

            strcpy(tmp, irest);
        } /* while tmp */

        strcpy(tmpc, rest);
    } /* while tmpc */

    return(rtn);
} /* verifyPluralVariants */

