int verifyOKEntryTerm(char *entry_term)
{
    int rtn = TRUE;
    long eLen = (long)strlen(entry_term);
    char tmp[eLen + 1], tmpc[eLen + 1], rest[eLen + 1];
    int done, okBigger;
    long i, len, numAlphaNum;

    done = FALSE;
    okBigger = FALSE;

    /* Trim off the surrounding brackets if present */

    if(entry_term[0] == '[')
    {
        strcpy(tmpc, "");
        sscanf(entry_term, "[%[^\n]", tmpc);
        len = (long)strlen(tmpc);
        if((len > 1) && (tmpc[len - 1] == ']'))
          tmpc[len - 1] = '\0';
    } /* fi */

    else
     strcpy(tmpc, entry_term);

    while(!done && (strlen(tmpc) > 0))
    {
        strcpy(tmp, "");
        strcpy(rest, "");

        /* Parsing: "Vegetable"-ab-12-"vegetable"-adj-0, */

        if(tmpc[0] == '"')
          sscanf(tmpc, "\"%*[^\"]\"%*[^\"]\"%[^\"]\"-%*[^-]-%*d%[^\n]",
                 tmp, rest);
        else
          sscanf(tmpc, "%*[^\"]\"%*[^\"]\"%*[^\"]\"%[^\"]\"-%*[^-]-%*d%[^\n]",
                 tmp, rest);

       /* Only want 4 char words or longer to review (ignore 3 or less)
          A few exceptions -
          UK, U.S, CT (computed tomography), Ti (Titanium), NA (Sodium),
          NO (Nitric Oxide), Hg (Mercury), Li (Lithium), 
          MRI (Magnetic Resonance Imaging), DNA, RNA, 
          PCR (Polymerase Chain Reaction),
          NMR (Magnetic Resonance Spectroscopy), Mus (Mice), Dog, Cat, Bee
          BCG (BCG Vaccine)
       */

        len = (long)strlen(tmp);
        if(len > 0)
        {
            if((strcmp(tmp, "UK") == 0) || (strcmp(tmp, "U.S") == 0) ||
               (strcmp(tmp, "CT") == 0) || (strcmp(tmp, "Ti") == 0) ||
               (strcmp(tmp, "Na") == 0) || (strcmp(tmp, "NO") == 0) ||
               (strcmp(tmp, "Hg") == 0) || (strcmp(tmp, "Li") == 0) ||
               (strcmp(tmp, "US") == 0) || (strcmp(tmp, "MRI") == 0) ||
               (strcmp(tmp, "DNA") == 0) || (strcmp(tmp, "PCR") == 0) ||
               (strcmp(tmp, "RNA") == 0) || (strcmp(tmp, "NMR") == 0) ||
               (strcmp(tmp, "Mus") == 0) || (strcmp(tmp, "pig") == 0) ||
               (strcmp(tmp, "Pig") == 0) || (strcmp(tmp, "rat") == 0) ||
               (strcmp(tmp, "Rat") == 0) || (strcmp(tmp, "USA") == 0) ||
               (strcmp(tmp, "U.S.A") == 0) || (strcmp(tmp, "Dog") == 0) ||
               (strcmp(tmp, "dog") == 0) || (strcmp(tmp, "Cat") == 0) ||
               (strcmp(tmp, "cat") == 0) || (strcmp(tmp, "Bee") == 0) ||
               (strcmp(tmp, "Pb") == 0) ||
               (strcmp(tmp, "bee") == 0) || (strcmp(tmp, "BCG") == 0))
            {
               done = TRUE;
               okBigger = TRUE;
            } /* fi */

            else if(len > 3)
            {
                numAlphaNum = 0;
                for(i = 0; i < len; i++)
                   if(isalnum(tmp[i]))
                     numAlphaNum++;

                if(numAlphaNum > 3)
                {
                    done = TRUE;
                    okBigger = TRUE;
                } /* fi */
            } /* else fi */
        } /* fi */

        strcpy(tmpc, rest);
    } /* while tmpc */

    if(!okBigger)
      rtn = FALSE;

    return(rtn);
} /* verifyOKEntryTerm */

