int verifyOKMHEntryTerm(char *MMI_term, char *entry_term)
{
    int rtn = FALSE;
    long eLen = (long)strlen(entry_term);
    long MLen = (long)strlen(MMI_term);
    char tmp[eLen + 1], tmpc[eLen + 1], tmp2[eLen + 1],
         word[eLen + 1], rest[eLen + 1], irest[eLen + 1],
         tword[eLen + 1], wword[eLen + 1], lcMMI_term[MLen + 1],
         lcWord[eLen + 1];
    int MMI_found[MLen + 1];
    int ET_found[eLen + 1];
    char *foo = NULL;
    int found, localDebug;
    float MMI_pct, ET_pct;
    long i, j, diff, pos, len, wLen, MMI_fctr;

    localDebug = FALSE;

    if(localDebug)
      fprintf(fout, "verifyOKMHEntryTerm(%s, %s)\n", MMI_term, entry_term);

    strcpy(lcMMI_term, MMI_term);
    for(i = 0; i < MLen; i++)
    {
       if(isupper(lcMMI_term[i]))
         lcMMI_term[i] = tolower(lcMMI_term[i]);
    } /* for */

    strcpy(tmpc, entry_term);
    while(!rtn && (strlen(tmpc) > 0))
    {
        strcpy(tmp, "");
        strcpy(rest, "");
        sscanf(tmpc, "%[^:]:%[^\n]", tmp, rest);

        while(!rtn && (strlen(tmp) > 0))
        {
            foo = strstr(tmp, "\",\"");
            strcpy(tmp2, "");
            strcpy(irest, "");
            len = (long)strlen(tmp);
            if(foo == NULL)
               diff = len;
            else
               diff = foo - tmp;

            for(i = 0; i < diff; i++)
              tmp2[i] = tmp[i];
            tmp2[diff] = '\0';

            pos = 0;
            for(i = (diff + 1); i < len; i++)
              irest[pos++] = tmp[i];
            irest[pos] = '\0';

            strcpy(word, "");
            strcpy(tword, "");
            if(tmp2[0] == '"')
              sscanf(tmp2, "\"%*[^\"]\"%[^\"]\"%*[^\"]\"%[^\"]",
                     tword, word);
            else
              sscanf(tmp2, "%*[^\"]\"%[^\"]\"%*[^\"]\"%[^\"]",
                     tword, word);

            wLen = (long)strlen(word);
            if(wLen > 0)  /* Only want up to 2 char words to review - or UK */
            {
                for(i = 0; i < MLen; i++)
                   MMI_found[i] = FALSE;

                strcpy(lcWord, word);
                for(i = 0; i < wLen; i++)
                {
                    if(isupper(lcWord[i]))
                      lcWord[i] = tolower(lcWord[i]);
                    ET_found[i] = FALSE;
                } /* for */

                MMI_fctr = 0;
                for(i = 0; i < MLen; i++)
                {
                    found = FALSE;
                    for(j = 0; !found && (j < wLen); j++)
                    {
                        if(!ET_found[j] && (lcMMI_term[i] == lcWord[j]))
                        {
                            found = TRUE;
                            ET_found[j] = TRUE;
                            MMI_found[i] = TRUE;
                            MMI_fctr++;
                        } /* fi */
                    } /* for */
                } /* for */

                MMI_pct = ((MMI_fctr * 1.0) / (MLen * 1.0)) * 100.0;
                ET_pct = ((MMI_fctr * 1.0) / (wLen * 1.0)) * 100.0;

                if(localDebug)
                {
                    fprintf(fout, "Found %ld/%ld = %0.2f%% and %0.2f%%\n",
                           MMI_fctr, MLen, MMI_pct, ET_pct);

                    fprintf(fout, "Matches: %s vs. %s\n", lcMMI_term, lcWord);

                    for(i = 0; i < MLen; i++)
                    {
                        if(MMI_found[i])
                          fprintf(fout, "%c", lcMMI_term[i]);
                        else
                          fprintf(fout, " ");
                    } /* for */
                    fprintf(fout, "\n");

                    for(i = 0; i < wLen; i++)
                    {
                        if(ET_found[i])
                          fprintf(fout, "%c", lcWord[i]);
                        else
                          fprintf(fout, " ");
                    } /* for */
                    fprintf(fout, "\n");
                } /* fi localDebug */

                if((MMI_pct > 59.99) && (ET_pct > 59.99))
                  rtn = TRUE;
            } /* fi */

            strcpy(tmp, irest);
        } /* while tmp */

        strcpy(tmpc, rest);
    } /* while tmpc */

    return(rtn);
} /* verifyOKMHEntryTerm */



