void checkIncorrectGeos()
{
   int j, n, k, ok, found, haveGEO;
   long i;
   char *mh, *et, tmp[SMALL_LINE + 1], term[SMALL_LINE + 1],
        rest[SMALL_LINE + 1];
   char **rows;
   long z, lenMH, lenET, lenT;

   for(i = 0; i < mt_count; i++)
   {
      ok = FALSE;
      lenET = 0;
      if((mt_table[i].num_treecodes > 0) && mt_table[i].oktoprint &&
         !mt_table[i].forcePrint)
      {
         haveGEO = found = FALSE;
         lenMH = (long)strlen(mt_table[i].mh);
         lenET = (long)strlen(mt_table[i].entry_term);

         for(j = 0; !haveGEO && (j < mt_table[i].num_treecodes); j++)
         {
             if((mt_table[i].treecodes[j][0] == 'Z') &&
                (strcmp(mt_table[i].treecodes[j], "Z01.639") != 0))
               haveGEO = TRUE;
         } /* for */

         /* If we have a geographical, verify it's ok by checking to see
            if whole geo is in the entry term.
            Convert to lowercase and see if mh is in et
         */

         if((lenET > 0) && haveGEO)
         {
             ok = FALSE;
             mh = (char *)strdup(mt_table[i].mh);
             et = (char *)strdup(mt_table[i].entry_term);
             for(z = 0; z < lenMH; z++)
             {
                if(isupper(mh[z]))
                  mh[z] = tolower(mh[z]);
             } /* for */

             for(z = 0; z < lenET; z++)
             {
                if(isupper(et[z]))
                  et[z] = tolower(et[z]);
             } /* for */

             if(strstr(et, mh) != NULL)
             {
                 ok = TRUE;
                 if(RTM_Debug)
                 {
                    fprintf(fout, "GEO Saved MH found in ET Rule: %s|%s\n",
                            mt_table[i].mh, mt_table[i].dui);
                    fflush(fout);
                 } /* fi */
             } /* fi */

             /* Handle USA/UK/Korea separately */

             if(!ok)
             {
                 if((strcmp(mh, "united states") == 0) &&
                    (strstr(et, "usa") != NULL))
                   ok = TRUE;

                 else if((strcmp(mh, "great britain") == 0) &&
                    (strstr(et, "uk") != NULL))
                   ok = TRUE;

                 else if((strcmp(mh, "republic of korea") == 0) &&
                    (strstr(et, "korea") != NULL))
                   ok = TRUE;

                 if(ok && RTM_Debug)
                 {
                    fprintf(fout, "GEO Saved US/UK Rule: %s|%s\n",
                            mt_table[i].mh, mt_table[i].dui);
                    fflush(fout);
                 } /* fi */
             } /* fi !ok */

             /* 10/18/08 - JGM
                If still not ok, one last chance with lookup of new
                GEO table that has entry terms and view terms.  Go through
                each of the entry terms and see if one of them matches to
                anything in the trigger ET.
             */

             if(!ok)
             {
                 query_btree(GEODBS, mt_table[i].mh, &rows, &n);
                 if((rows != NULL) && (n > 0))
                 {
                    for(j = 0; j < n; j++)
                    {
                       strcpy(tmp, "");
                       sscanf(rows[j], "%*[^|]|%[^\n]", tmp);
                       while(!ok && (strlen(tmp) > 0))
                       {
                           strcpy(term, "");
                           strcpy(rest, "");
                           sscanf(tmp, "%[^:]:%[^\n]", term, rest);
                           strcpy(tmp, rest);
                           lenT = (long)strlen(term);
                           if(lenT > 0)
                           {
                               for(z = 0; z < lenT; z++)
                               {
                                   if(isupper(term[z]))
                                     term[z] = tolower(term[z]);
                               } /* for */

                               if(strstr(et, term) != NULL)
                               {
                                   ok = TRUE;
                                   if(RTM_Debug)
                                   {
                                      fprintf(fout,
                                                "GEO Saved GEO ET DB Rule:");
                                      fprintf(fout, "%s|%s by \"%s\"\n",
                                         mt_table[i].mh, mt_table[i].dui, term);
                                      fflush(fout);
                                   } /* fi RTM_Debug */
                               } /* fi */
                           } /* fi */
                       } /* while */

                       free(rows[j]);
                    } /* for */
                 } /* fi rows */

                 if(rows != NULL)
                   free(rows);
             } /* fi !ok */

             free(mh); free(et);
         } /* fi lenET > 0 && haveGEO) */
      } /* fi */

      /* 10/18/08 - JGM: Add check for MMI&PRC recommended */

      if(!ok && haveGEO && mt_table[i].oktoprint && !mt_table[i].forcePrint)
      {
           if(mt_table[i].paths[MMI] && mt_table[i].paths[PRC])
           {
               ok = TRUE;
               if(RTM_Debug)
               {
                   fprintf(fout, "GEO Saved MMI+PRC Rule: %s|%s\n",
                           mt_table[i].mh, mt_table[i].dui);
                   fflush(fout);
               } /* fi */
           } /* fi */
      } /* fi */

      if(!ok && !mt_table[i].forcePrint && haveGEO)
      {
          mt_table[i].score = 0;
          mt_table[i].oktoprint = FALSE;
          if(RTM_Debug)
          {
              fprintf(fout, "Ignoring GEO MH: %s|%s|%d|%d", mt_table[i].mh,
                       mt_table[i].dui, mt_table[i].paths[MMI],
                       mt_table[i].paths[PRC]);

              if(lenET > 0)
                fprintf(fout, " (%s) Not Found in ET\n",
                        mt_table[i].entry_term);
              else
                fprintf(fout, " Not Found in ET\n");
              fflush(fout);
          } /* fi */
      } /* !ok */
   } /* for each MH */
} /* checkIncorrectGeos */


