void loadPTs()
{
    char line[MAXLINE + 1];
    char DUI[25];
    char CUI[25];
    char PT[MAXLINE + 1];
    char lcPT[MAXLINE + 1];
    char singularPT[MAXLINE + 1];
    char lcSingularPT[MAXLINE + 1];
    char asTopic[MAXLINE + 1];
    long i, len, lenS;
    FILE *fp;

    fp = fopen(CATPTS, "r");

    while(fgets(line, MAXLINE, fp) != (char *)NULL)
    {
        strcpy(DUI, "");
        strcpy(PT, "");
        strcpy(singularPT, "");
        strcpy(asTopic, "");
        sscanf(line, "%[^|]|%[^|]|%[^|]|%[^|]|%[^\n]", DUI, CUI, PT, singularPT, asTopic);
        len = (long)strlen(PT);
        lenS = (long)strlen(singularPT);
        if((strlen(DUI) > 0) && (len > 0) && (lenS > 0))
        {
            if(strcmp(PT, "Congress") == 0)
              catCongressPos = numPTs;

            else if(strcmp(PT, "Legal Cases") == 0)
              catLegalCasesPos = numPTs;

            else if(strcmp(PT, "Legislation") == 0)
              catLegislationPos = numPTs;

            else if(strcmp(PT, "Statistics") == 0)
              catStatsPos = numPTs;

            else if(strcmp(PT, "Examination Questions") == 0)
              catExamQuestPos = numPTs;

            strcpy(lcPT, PT);
            for(i = 0; i < len; i++)
            {
                if(isupper(lcPT[i]))
                  lcPT[i] = tolower(lcPT[i]);
            } /* for */

            strcpy(lcSingularPT, singularPT);
            for(i = 0; i < len; i++)
            {
                if(isupper(lcSingularPT[i]))
                  lcSingularPT[i] = tolower(lcSingularPT[i]);
            } /* for */

            catPTs[numPTs].DUI = (char *)strdup(DUI);
            catPTs[numPTs].CUI = (char *)strdup(CUI);
            catPTs[numPTs].PT = (char *)strdup(PT);
            catPTs[numPTs].lcPT = (char *)strdup(lcPT);
            catPTs[numPTs].singularPT = (char *)strdup(singularPT);
            catPTs[numPTs].lcSingularPT = (char *)strdup(lcSingularPT);
            catPTs[numPTs].asTopic = (char *)strdup(asTopic);
            numPTs++;
        } /* fi */

        else
        {
             fprintf(stderr, "ERROR - Bad PT Line: #%s#\n", line);
             fflush(stderr);
        } /* else */
    } /* while */

    fclose(fp);
} /* loadPTs */


