/***************************************************************************
*
*  cleanMHSH --
*
*      This 
*
***************************************************************************/

void cleanMHSH()
{
    long i;

    if(numMHSHs > maxMHSHs)
       maxMHSHs = numMHSHs;

    for(i = 0; i < numMHSHs; i++)
    {
        if(MHSHs[i].MHSH != NULL)
          free(MHSHs[i].MHSH);

        if(MHSHs[i].PMID != NULL)
          free(MHSHs[i].PMID);
        MHSHs[i].score = 0.0;
        MHSHs[i].IM = FALSE;
        MHSHs[i].ctr = 0;
    } /* for */

    numMHSHs = 0;
} /* cleanMHSH */

/***************************************************************************
*
*  findMHSH --
*
*      This 
*
***************************************************************************/

void findMHSH(char *term, char *PMID, int IM, float score)
{
    long i, pos;
    int found = FALSE;

    for(i = 0; !found && (i < numMHSHs); i++)
    {
        if(strcmp(term, MHSHs[i].MHSH) == 0)
        {
            found = TRUE;
            pos = i;
        } /* fi */
    } /* for */

    if(found)
    {
        MHSHs[pos].score += score;
        if(IM)
          MHSHs[pos].IM = TRUE;

        MHSHs[pos].ctr++;
    } /* fi */

    else
    {
        MHSHs[numMHSHs].PMID = strdup(PMID);
        MHSHs[numMHSHs].score = score;
        MHSHs[numMHSHs].IM = IM;
        MHSHs[numMHSHs].ctr = 1;
        MHSHs[numMHSHs].MHSH = (char *)strdup(term);
        numMHSHs++;
    } /* else */
} /* findMHSH */

