/***************************************************************************
*
*  check_CatPTs --
*
*      This 
*
***************************************************************************/

void check_CatPTs(long highScore)
{
   long i;
   long diff, lenT, len3, tail;
   int ok, index;
   char *tmp;
   char *tmp2 = (char *)malloc(cit_len + 1);
   char *tmp3 = (char *)malloc(cit_len + 1);
   char from[MAXLINE + 1];

   /* Initialize everything */

   for(i = 0; i < numPTs; i++)
   {
       ok = FALSE;
       strcpy(tmp3, globalTitle);
       strcat(tmp3, "\n");
       strcat(tmp3, globalAbstract);
       lenT = (long)strlen(catPTs[i].lcSingularPT);
       while(!ok && (strlen(tmp3) > 0))
       {
          len3 = (long)strlen(tmp3);
          tmp = strstr(tmp3, catPTs[i].lcSingularPT);
          strcpy(from, "Lookup");

          if(tmp != NULL)
          {
             diff = (tmp - tmp3) - 1;
             tail = diff + lenT + 1;

             if((diff <= 0) || isspace(tmp3[diff]) || ispunct(tmp3[diff]))
             {
                if((tail >= len3) || ((tail < len3) && 
                   (isspace(tmp3[tail]) || ispunct(tmp3[tail]) || 
                                (tmp3[tail] == 's'))))
                {
                    /* IF we have an 's' ending, make sure we are really
                       at the end of a word, sentence, or citation.
                    */

                    if(tmp3[tail] == 's')
                    {
                       if(((tail + 1) > len3) || (((tail + 1) < len3) && 
                          (isspace(tmp3[tail + 1]) || ispunct(tmp3[tail + 1]))))
                       {
                          ok = TRUE;
                          sprintf(from, "CatPT Lookup:%ss\0",
                                  catPTs[i].lcSingularPT);
                       } /* fi */
                    } /* fi */

                    else
                    {
                       ok = TRUE;
                       sprintf(from, "CatPT Lookup:%s\0",
                               catPTs[i].lcSingularPT);
                    } /* else */
                } /* fi 's' */
             } /* else fi */

             else if((tail >= len3) &&
                     (isspace(tmp3[diff]) || ispunct(tmp3[diff])))
             {
                ok = TRUE;
                sprintf(from, "CatPT Lookup:%s\0", catPTs[i].lcSingularPT);
             } /* else fi */

             if(!ok)
             {
                 strcpy(tmp2, (tmp3 + diff + lenT));
                 strcpy(tmp3, tmp2);
             } /* else */
          } /* fi */
          else
            strcpy(tmp3, "");
       } /* while */

       if(ok)
         addCatPT(i, FALSE, FALSE, highScore, from);
   } /* for */

   free(tmp2); free(tmp3);
} /* check_CatPTs */

