    {
        fprintf(fout, "\nforceNewTerms\n");
        fflush(fout);
    } /* fi RTM_Debug */
/*
fprintf(fout, "TI: #%s#\n", fITXObase);
fprintf(fout, "AB: #%s#\n", fIAXObase);
*/

