/***************************************************************************
*
*  addPositionalInfo --
*
*      The assumption is that we have a postionalInfo element for each 
*   entry_term element.
*
***************************************************************************/

void addPositionalInfo(long mtPos, char *entry_term, char *positionalInfo)
{
    char tmp[MAXLINE + 1], tmpc[MAXLINE + 1], tmp2[MAXLINE + 1],
         textWord[SMALL_LINE + 1], rest[MAXLINE + 1], irest[MAXLINE + 1],
         ET_MH[SMALL_LINE + 1], pITmp[SMALL_LINE + 1], pIWord[SMALL_LINE + 1],
         pIRest[SMALL_LINE + 1], tj[SMALL_LINE + 1], pIJGM[SMALL_LINE + 1],
         pIJGM_tmp[SMALL_LINE + 1], pIJGM_Rest[SMALL_LINE + 1];
    char *foo = NULL;
    long i, diff, pos, len, tjLen, tLen;

    if(RTM_Debug)
    {
        fprintf(fout, "ET: #%s#  pI: #%s#\n", entry_term, positionalInfo);
        fflush(fout);
    } /* fi */

    strcpy(pITmp, positionalInfo);
    strcpy(tmpc, entry_term);

    while(strlen(tmpc) > 0)
    {
        strcpy(tmp, "");
        strcpy(rest, "");

        foo = strstr(tmpc, ",\"");
        tLen = (long)strlen(tmpc);

        if(foo == NULL)
          diff = tLen;

        else
          diff = foo - tmpc;

        for(i = 0; i < diff; i++)
          tmp[i] = tmpc[i];
        tmp[diff] = '\0';

        pos = 0;
        for(i = (diff + 1); i < tLen; i++)
          rest[pos++] = tmpc[i];
        rest[pos] = '\0';

        strcpy(tmpc, rest);
        strcpy(textWord, "");
        strcpy(ET_MH, "");
        strcpy(tj, "");
        if(tmp[0] == '"')
          sscanf(tmp, "\"%[^\"]\"-%[^\"]\"%[^\"]", ET_MH, tj, textWord);
        else
          sscanf(tmp, "%*[^\"]\"%[^\"]\"-%[^\"]\"%[^\"]", ET_MH, tj, textWord);

        if(strlen(textWord) > 0)
        {
            tjLen = (long)strlen(tj);
            if(tjLen > 0)
              tj[tjLen - 1] = '\0';  /* Take of ending dash */

            if(RTM_Debug)
            {
                fprintf(fout, "textWord: #%s#  tj: #%s#  ET_MH: #%s#\n",
                        textWord, tj, ET_MH);
                fflush(fout);
            } /* fi */

            strcpy(pIWord, "");
            strcpy(pIRest, "");
            sscanf(pITmp, "%[^;];%[^\n]", pIWord, pIRest);
            strcpy(pITmp, pIRest);

            if(RTM_Debug)
            {
                fprintf(fout, "pIWord: #%s#  pIRest: #%s#\n", pIWord, pIRest);
                fflush(fout);

                if(strlen(pIWord) == 0)
                {
                    fprintf(stderr, "ERROR - Mis-matched ET & pI: %s|%s|%s\n",
                            mt_table[mtPos].mh, entry_term, positionalInfo);
                    fflush(stderr);
                } /* fi */
            } /* fi */

            /* Starting with MM16, all instances of the positional information
               is presented by MM instead of just the first one.
               For example, [1040/5],[1147/5]
            */

            if(strlen(pIWord) > 0)
            {
                if(strchr(pIWord, '[') == NULL)
                  addETWord(mtPos, textWord, pIWord);
                else
                {
                    strcpy(pIJGM_tmp, pIWord);
                    while(strlen(pIJGM_tmp) > 0)
                    {
                        strcpy(pIJGM, "");
                        strcpy(pIJGM_Rest, "");
                        sscanf(pIJGM_tmp, "[%[^]],%[^\n]", pIJGM, pIJGM_Rest);
                        if(strlen(pIJGM) > 0)
                          addETWord(mtPos, textWord, pIJGM);
                        strcpy(pIJGM_tmp, pIJGM_Rest);
                    } /* while */
                } /* else */
            } /* fi */

            else
            {
                fprintf(stderr, "ERROR - Mis-matched ET & pI: %s|%s|%s\n",
                        mt_table[mtPos].mh, entry_term, positionalInfo);
                fflush(stderr);
            } /* else */
        } /* fi */
    } /* while tmpc */
} /* addPositionalInfo */

/***************************************************************************
*
*  addETWord --
*
*      This
*
***************************************************************************/

void addETWord(long mtPos, char *textWord, char *pIWord)
{
    int found, firstTime;
    long i, len, pos, startPos, pLen;
    char tmp[SMALL_LINE + 1], rest[SMALL_LINE + 1];

    len = (long)strlen(textWord);

    /* Need to go through each of the positionalInformations for this term */

    firstTime = TRUE;
    strcpy(tmp, pIWord);
    while(strlen(tmp) > 0)
    {
        strcpy(rest, "");
        startPos = pLen = -1;
        if(strchr(tmp, '/') == NULL)
          sscanf(tmp, "%ld^%ld;%[^\n]", &startPos, &pLen, rest);
        else
          sscanf(tmp, "%ld/%ld,%[^\n]", &startPos, &pLen, rest);

        /* Compensate for the PMID- 00000000\nTI  - " we added to the
           free text to put the positional info back to where it should be.
           The user may have actually used 00000000, so check to make sure
           we don't go below zero for the startPos.
        */

        if(haveFreeText)
        {
            if(startPos > 20)
              startPos -= 21;
        } /* fi */

        if((startPos > -1) && (pLen > -1))
        {
            found = FALSE;
            for(i = 0; !found && (i < mt_table[mtPos].numPIs); i++)
            {
                if(startPos == mt_table[mtPos].PIs[i].startPos)
                  found = TRUE;
            } /* for */

            if(!found)
            {
                pos = mt_table[mtPos].numPIs;
                if(pos < MAX_PIs)
                {
                     if(firstTime)
                       mt_table[mtPos].PIs[pos].isStart = TRUE;
                     else
                       mt_table[mtPos].PIs[pos].isStart = FALSE;
                     mt_table[mtPos].PIs[pos].startPos = startPos;
                     mt_table[mtPos].PIs[pos].endPos = startPos + len;
                     mt_table[mtPos].PIs[pos].len = pLen;
                     if(pLen == len)
                       mt_table[mtPos].PIs[pos].hasBreak = FALSE;
                     else
                       mt_table[mtPos].PIs[pos].hasBreak = TRUE;
                     mt_table[mtPos].numPIs++;
                     firstTime = FALSE;
                } /* fi */

                else
                {
                     fprintf(stderr, "ERROR - Too many ETs: %s|%s|\n", muid,
                             mt_table[mtPos].mh);
                     fflush(stderr);
                } /* else */
            } /* fi */
        } /* fi */

        else
        {
            fprintf(stderr, "ERROR: %s|PI|%s|%s|\n", muid, pIWord, tmp);
            fflush(stderr);
        } /* else */

        strcpy(tmp, rest);
    } /* while */
} /* addETWord */

