/***************************************************************************
*
*  checkForceMH --
*
*      This
*
***************************************************************************/

void checkForceMH(long mtPos)
{
    long j, len, lenET, lenT;
    int i, n, done, found, numETs;
    char term[MAXLINE + 1], tmpJ[SMALL_LINE + 1];
    char *lcMH, *tmp, *tmp2, *tmp3, *tmp4, *loc, *eterm;
    char *ETs[25], *ETsNorm[25];
    char **rows;

    lcMH = (char *)strdup(mt_table[mtPos].mh);
    len = (long)strlen(lcMH);
    for(j = 0; j < len; j++)
    {
        if(isupper(lcMH[j]))
          lcMH[j] = tolower(lcMH[j]);
    } /* for */

    /* Retrieve list of terms from actual text */

    numETs = 0;
    lenET = (long)strlen(mt_table[mtPos].entry_term);
    tmp = (char *)malloc(lenET + 1);
    tmp2 = (char *)malloc(lenET + 1);
    tmp3 = (char *)malloc(lenET + 1);
    tmp4 = (char *)malloc(lenET + 1);
    loc = (char *)malloc(lenET + 1);
    eterm = (char *)malloc(lenET + 1);

    strcpy(tmp, mt_table[mtPos].entry_term);
    done = FALSE;
    while(!done && (strlen(tmp) > 0))
    {
       strcpy(tmp2, "");
       strcpy(tmp3, "");
       sscanf(tmp, "%[^:]:%[^\n]", tmp2, tmp3);

       while(!done && (strlen(tmp2) > 0))
       {
          strcpy(eterm, "");
          strcpy(tmp4, "");
          strcpy(loc, "");
          sscanf(tmp2, "%*[^\"]\"%[^\"]\"-%[^,]%[^\n]", eterm, loc, tmp4);

          /* Only interested in items from Title */

          if((strlen(eterm) > 0) && (strstr(loc, "ti-") != NULL))
          {
              if(numETs < 25)
              {
                  ETsNorm[numETs] = strdup(eterm);

                  for(j = 0; j < strlen(eterm); j++)
                  {
                      if(isupper(eterm[j]))
                        eterm[j] = tolower(eterm[j]);
                  } /* for */

                  ETs[numETs++] = strdup(eterm);
              } /* fi */
          } /* fi */

          strcpy(tmp2, tmp4);
       } /* while */

       strcpy(tmp, tmp3);
    } /* while */

    found = FALSE;
    for(j = 0; !found && (j < numETs); j++)
    {
        if(strcmp(ETs[j], lcMH) == 0)
          found = TRUE;

        if(RTM_Debug)
        {
            fprintf(fout, "checkForceMH -- ETs[%ld]: %s  -- Found: %d\n",
                    j, ETs[j], found);
            fflush(fout);
        } /* fi */
    } /* for */

    /* If we don't find the actual MeSH Heading, let's see if we have one
       of it's entry terms.
    */

    if(!found)
    {
        query_btree(ETDUI, mt_table[mtPos].dui, &rows, &n);

        if((rows != NULL) && (n > 0))
        {
           for(i = 0; i < n; i++)
           {
                if(rows[i] != NULL )
                {
                   strcpy(term, "");
                   sscanf(rows[i], "%*[^|]|%[^\n]", term);
                   for(j = 0; !found && (j < numETs); j++)
                   {
                       if(strcmp(term, ETs[j]) == 0)
                         found = TRUE;
                   } /* for */

                   if(RTM_Debug)
                   {
                       fprintf(fout,
                               "checkForceMH - Rows[%d]: %s  -- Found: %d\n",
                               i, rows[i], found);
                       fflush(fout);
                   } /* fi */

                   free(rows[i]);
                } /* fi */
           } /* for */
        } /* fi */

        if(rows != NULL)
          free(rows);
    } /* fi !found */

    /* If we still haven't found it, look to see if it's the first part
       of an organism name or the like. E.g.,
          Chromohalobacter  for  Chromohalobacter salexigens

       This test is case sensitive and only looks at the head of the
       entry term to see if we have a match followed by a space, dash,
       or comma.
    */

    if(!found)
    {
        sprintf(tmpJ, "%s \0", mt_table[mtPos].mh);
        lenT = (long)strlen(tmpJ);
        for(j = 0; !found && (j < numETs); j++)
        {
            if(strncmp(ETsNorm[j], tmpJ, lenT) == 0)
              found = TRUE;

            if(RTM_Debug)
            {
               fprintf(fout,
                       "checkForceMH -- HEAD: %s [%ld] %s  -- Found: %d\n",
                       tmpJ, j, ETsNorm[j], found);
               fflush(fout);
            } /* fi */
        } /* for */
    } /* fi */

    if(!found)
    {
        sprintf(tmpJ, "%s,\0", mt_table[mtPos].mh);
        lenT = (long)strlen(tmpJ);
        for(j = 0; !found && (j < numETs); j++)
        {
            if(strncmp(ETsNorm[j], tmpJ, lenT) == 0)
              found = TRUE;

            if(RTM_Debug)
            {
               fprintf(fout,
                     "checkForceMH -- HEAD Comma: %s [%ld] %s  -- Found: %d\n",
                     tmpJ, j, ETsNorm[j], found);
               fflush(fout);
            } /* fi */
        } /* for */
    } /* fi */

    if(!found)
    {
        sprintf(tmpJ, "%s-\0", mt_table[mtPos].mh);
        lenT = (long)strlen(tmpJ);
        for(j = 0; !found && (j < numETs); j++)
        {
            if(strncmp(ETsNorm[j], tmpJ, lenT) == 0)
              found = TRUE;

            if(RTM_Debug)
            {
               fprintf(fout,
                       "checkForceMH -- HEAD Dash: %s [%ld] %s  -- Found: %d\n",
                       tmpJ, j, ETsNorm[j], found);
               fflush(fout);
            } /* fi */
        } /* for */
    } /* fi */

    /* ADDED 092111 - JGM -- this means it was likely turned off by someone */

    if(found && ((mt_table[mtPos].score == 0) || !mt_table[mtPos].oktoprint))
       found = FALSE;

    /* Added 020613 - JGM - Don't bump if on the MH_Exclude list */

    if(found)
    {
        if(isMHExclude(mt_table[mtPos].mh, FALSE))
          found = FALSE;

        else if(strcmp(mt_table[mtPos].mh, "Fats") == 0)
          found = FALSE;

        else if((strcmp(mt_table[mtPos].mh, "Deer") == 0) && 
                foundInText("DEER", TRUE, FALSE))
          found = FALSE;

        /* Added 4/7/14 - % Correct
            Yeasts - 20.59%
            Amyloid - 14.29%
            Antibodies - 27.78%
            Diptera - 21.23%  -- Removed "Fly"
            Orthomyxoviridae - 24.80%
            Antibodies, Anti-Idiotypic - 10.34%
        */

        else if(strcmp(mt_table[mtPos].mh, "Yeasts") == 0)
          found = FALSE;

        else if(strcmp(mt_table[mtPos].mh, "Amyloid") == 0)
          found = FALSE;

        else if(strcmp(mt_table[mtPos].mh, "Antibodies") == 0)
          found = FALSE;

        else if(strcmp(mt_table[mtPos].mh, "Orthomyxoviridae") == 0)
          found = FALSE;

        else if(strcmp(mt_table[mtPos].mh, "Antibodies, Anti-Idiotypic") == 0)
          found = FALSE;

        else if(strcmp(mt_table[mtPos].mh, "Silver") == 0)
        {
            if(!isLookForOK("silver"))
              found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "Plastics") == 0)
        {
            if(!isLookForOK("plastic"))
              found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "Diptera") == 0)
        {
            if(!isLookForOK("fly"))
              found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "Alnus") == 0)
        {
            if(!isLookForOK("alder"))
              found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "Songbirds") == 0)
        {
            if(!isLookForOK("robin"))
              found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "Butterflies") == 0)
        {
            if(!isLookForOK("butterfly") || !isLookForOK("butterflies"))
              found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "Lotus") == 0)
        {
            if(!isLookForOK("lotus"))
              found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "Cereals") == 0)
        {
            if(foundInText("grain size", FALSE, FALSE) ||
               foundInText("grain-size", FALSE, FALSE) ||
               foundInText("coarse-grain", FALSE, FALSE) ||
               foundInText("coarse grain", FALSE, FALSE) ||
               foundInText("magnetic grain", FALSE, FALSE))
                 found = FALSE;
        } /* else fi */

        else if(strcmp(mt_table[mtPos].mh, "SNARE Proteins") == 0)
        {
            if(!foundInText("SNARE", TRUE, FALSE))
                 found = FALSE;
        } /* else fi */
    } /* fi */

    if(found)
    {
        mt_table[mtPos].score += avgScore;
        mt_table[mtPos].forcePrint = TRUE;

        if(strlen(mt_table[mtPos].trigger) == 0)
           strcpy(mt_table[mtPos].trigger, "checkForceMH Boosted");

        else
        {
           strcat(mt_table[mtPos].trigger, ";");
           strcat(mt_table[mtPos].trigger, "checkForceMH Boosted");
        } /* fi */
    } /* fi */

    free(lcMH); free(tmp); free(tmp2); free(tmp3); free(tmp4);
    free(loc); free(eterm);

    for(j = 0; j < numETs; j++)
    {
        if(ETs[j])
          free(ETs[j]);

        if(ETsNorm[j])
          free(ETsNorm[j]);
    } /* for */
} /* checkForceMH */


/***************************************************************************
*
*  setupCutOff --
*
*      This
*
***************************************************************************/

void setupCutOff()
{
    long i, displayCount;

    /* Count forced first */

    displayCount = 0;
    for(i = 0; i < mt_count; i++)
    {
        if(mt_table[i].safe && mt_table[i].oktoprint &&
           mt_table[i].forcePrint && (mt_table[i].datatype != CT) &&
           (mt_table[i].datatype != SH))
          displayCount++;
    } /* for */

    if(RTM_Debug)
      fprintf(fout, "Removing CutOff - Found %ld Forced\n", displayCount);

    for(i = 0; i < mt_count; i++)
    {
        if(mt_table[i].safe && mt_table[i].oktoprint &&
           !mt_table[i].forcePrint && (mt_table[i].datatype != CT) &&
           (mt_table[i].datatype != SH))
        {
            displayCount++;
            if(displayCount > 15)
            {
                mt_table[i].safe = FALSE;
                mt_table[i].oktoprint = FALSE;
                mt_table[i].score = -1;
                if(RTM_Debug)
                  fprintf(fout, "Removing CutOff (%ld) Rule: %s\n",
                          displayCount, mt_table[i].mh);
            } /* fi */
        } /* fi */
    } /* for */
} /* setupCutOff */


