/***************************************************************************
*
*  removeEMAIL --
*
*      This
*
***************************************************************************/

char *removeEMAIL(char *text)
{
    int done, c, found;
    long i, len, diff, start, pos, trigPos;
    char *rtn, *rtnLC, *foo, target[SMALL_LINE + 1];

    len = (long)strlen(text);
    rtn = strdup(text);
    rtnLC = strdup(text);
    for(i = 0; i < len; i++)
    {
        if(isupper(rtnLC[i]))
          rtnLC[i] = tolower(rtnLC[i]);
    } /* for */

    strcpy(target, "");
    foo = strchr(rtnLC, '@');
    if(foo != NULL)
    {
        c = 0;
        while(foo != NULL)
        {
            pos = 0;
            diff = foo - rtnLC;
            done = FALSE;

            start = diff;
            while((start >= 0) && (rtn[start] != ' ') && (rtn[start] != ':'))
               start--;
            if(start < diff)
              start++;

            for(i = start; !done && (i < len); i++)
            {
                if((rtn[i] == ' ') || (rtn[i] == '\n'))
                  done = TRUE;

                else
                  target[pos++] = rtnLC[i];
            } /* for */

            target[pos] = '\0';

            found = FALSE;
            for(i = 0; !found && (i < numTLDNA); i++)
            {
               if(strstr(target, TLDNAs[i]) != NULL)
               {
                   found = TRUE;
                   trigPos = i;
               } /* fi */
            } /* for */

            if(found)
            {
                if(RTM_Debug)
                {
                    fprintf(fout, "Removing Email Address: #%s#  Trigger: %s\n",
                            target, TLDNAs[trigPos]);
                    fflush(fout);
                } /* fi */

                for(i = 0; i < pos; i++)
                {
                    rtn[i + start] = 'x';
                    rtnLC[i + start] = 'x';
                } /* for */
            } /* fi */

            else
              rtnLC[diff] = 'x';

            strcpy(target, "");
            pos = 0;
            foo = strchr(rtnLC, '@');
        } /* while */
    } /* fi */

    free(rtnLC);
    return(rtn);
} /* removeEMAIL */


/***************************************************************************
*
*  remove_Last_Sentence_Support --
*
*      This
*
***************************************************************************/

char *remove_Last_Sentence_Support(char *text)
{
    int done;
    long i, len, pos, Spos;
    char *rtn, lastSent[SMALL_LINE + 1];

    len = (long)strlen(text);
    rtn = strdup(text);

    done = FALSE;
    pos = -1;
    i = len - 1;
    while(!done && (i > 0))
    {
        /* Make sure to ignore "No. ####" as begin of sentence */

        if((text[i] == ' ') && (text[i - 1] == '.'))
        {
            if(i > 3)
            {
                if((text [i - 2] == 'o') || (text [i - 2] == 'O'))
                {
                   if((text [i - 3] == 'n') || (text [i - 3] == 'N'))
                     done = FALSE;
                   else
                     done = TRUE;
                } /* fi */

                else
                  done = TRUE;
            } /* fi */
        } /* fi */

        if(done)
          pos = i;

        i--;
    } /* while */

    /* Now, build the last sentence if we can */

    if(pos >= 0)
    {
        strcpy(lastSent, "");
        Spos = 0;
        i = pos + 1;  /* Skip leading blank */
        while(i < len)
          lastSent[Spos++] = text[i++];
        lastSent[Spos] = '\0';

        /* If beginning of the last sentence starts with "Supported by", go
           ahead and remove.  Causing false positive recommendations for
           "China".

  Supported by National Natural Science Foundation of China (Grant No.30572046).
        */

        if(strncmp(lastSent, "Supported by", 12) == 0)
        {
            rtn[pos] = '\0';

            if(RTM_Debug)
            {
                fprintf(fout, "Last Sentence: #%s#\n", lastSent);
                fprintf(fout, "Supported by, trimming last sent: #%s#\n",
                        rtn);
                fflush(fout);
            } /* fi */
        } /* fi */
    } /* fi */

    return(rtn);
} /* remove_Last_Sentence_Support */


