char *remove_Active_Journal_Names(char *text)
{
    int okLeft, ok, rOK;
    long i, j, len, diff, start, gLen;
    char *rtn, *rtnLC, *foo, *tmp;
    FILE *fp;

    len = (long)strlen(text);
    rtn = strdup(text);
    rtnLC = strdup(text);

    for(i = 0; i < numAJNames; i++)
    {
        gLen = (long)strlen(activeJournalNames[i]);
        foo = strstr(rtnLC, activeJournalNames[i]);

        /* If regular case not found, check all UPPERCASE */

        if(foo == NULL)
        {
            tmp = strdup(activeJournalNames[i]);
            for(j = 0; j < gLen; j++)
            {
                if(islower(tmp[j]))
                  tmp[j] = toupper(tmp[j]);
            } /* for */

            foo = strstr(rtnLC, tmp);
            free(tmp);
        } /* fi */

        while(foo != NULL)
        {
            okLeft = ok = rOK = FALSE;
            diff = foo - rtnLC;

            if(diff == 0)
              okLeft = TRUE;

            else if(diff > 0)
            {
                if(isspace(rtnLC[diff - 1]) || ispunct(rtnLC[diff - 1]))
                  okLeft = TRUE;
            } /* else fi */

            if(okLeft)
            {
                if(((diff + gLen) >= len) || 
                   ispunct(rtnLC[diff + gLen]) || isspace(rtnLC[diff + gLen]))
                  rOK = TRUE;

                if(rOK)
                {
                    ok = TRUE;
                    if(RTM_Debug)
                      fprintf(fout, "Journal Name Removal (%s)\n",
                              activeJournalNames[i]);
                } /* fi */
             } /* fi */

             for(j = 0; (j < gLen) && ((j + diff) < len); j++)
             {
                 rtnLC[j + diff] = 'x';

                 if(ok)
                   rtn[j + diff] = 'x';
            } /* for */

            foo = strstr(rtnLC, activeJournalNames[i]);
        } /* while */
    } /* for */

    free(rtnLC);
    return(rtn);
} /* remove_Active_Journal_Names */


