/***************************************************************************
*
*  checkFirstLine --
*
*      This checks to see if we have either MTIFL or a MTIRJ journal
*
***************************************************************************/

void checkFirstLine(char *inJID)
{
    char line[MAXLINE + 1], JID[100], cmd[SMALL_LINE + 1];
    int isReview;
    FILE *fp;

    /* If designated as a MTI-AUTO citation, setup and no need to
       check on the journal.
       NOTE: ACCME also turns on MTI_AUTO
       NOTE: MTI_REVIEW also turns on MTI_AUTO & look4PTs
       NOTE: MTIA also turns on MTI_AUTO & look4PTs
    */

    /* If MTI_AUTO already set, we don't want to check to see if MTIR */

    if(!MTI_AUTO)
    {
        /* Check to see if this is a MTI_Review Journal 2/7/19 */

        sprintf(cmd, "grep \"|%s|\" %s\0", inJID, MTI_REVIEW_JOURNALS_FILE);
        fp = popen(cmd, "r");

        if(fp != NULL)
       {
            while(fgets(line, MAXLINE, fp) != (char *)NULL)
            {
                   if(line[0] != '#')  /* Maybe it has been deleted from program */
                   {
                       strcpy(JID, "");
                       sscanf(line, "%*[^|]|%[^|]", JID);
                       if(strcmp(JID, inJID) == 0)
                       {
                           MTI_REVIEW = TRUE;
                           if(!hasCON)
                             look4PTs = TRUE;
                           MTI_AUTO = TRUE;
                           showETs = FALSE;
                       } /* fi */
                   } /* fi */
            } /* while */
            pclose(fp);
        } /* fi */

        else
        {
            fprintf(stderr, "ERROR - Unable to open cmd: #%s#\n", cmd);
            fflush(stderr);
        } /* else */
    } /* fi !MTI_AUTO */

    /* Check to see if this is a MTIA Journal 10/18/19 */

    if(!MTI_REVIEW && !hasCON && !MTI_AUTO)
    {
        sprintf(cmd, "grep \"|%s|\" %s\0", inJID, MTI_AUTO_JOURNALS_FILE);
        fp = popen(cmd, "r");

        if(fp != NULL)
        {
            while(fgets(line, MAXLINE, fp) != (char *)NULL)
            {
                   if(line[0] != '#')
                   {
                       strcpy(JID, "");
                       sscanf(line, "%*[^|]|%[^|]", JID);
                       if(strcmp(JID, inJID) == 0)
                       {
                           if(!hasCON)
                             look4PTs = TRUE;
                           MTI_AUTO = TRUE;
                           showETs = FALSE;
                       } /* fi */
                   } /* fi */
            } /* while */

            pclose(fp);
        } /* fi */

        else
        {
            fprintf(stderr, "ERROR - Unable to open cmd: #%s#\n", cmd);
            fflush(stderr);
        } /* else */
    } /* fi */

    if(MTI_AUTO)
    {
        if(!hasCON)
          look4PTs = TRUE;
        firstLineJournal = TRUE;
        medFilter_set = TRUE;
        showETs = FALSE;
        starMHTI_set = FALSE;
        level1Filter = TRUE;
        firstLineReviewJournal = TRUE;

        if(strstr(default_TexToolHost, "ii-public") == NULL)
          doIL2RHybrid = TRUE;

        if(RTM_Debug)
        {
            if(hasCON)
               fprintf(fout, "Citation is Designated as \"MTI-AUTO Comment On\"\n");

            else if(MTI_REVIEW)
               fprintf(fout, "Citation is Designated as \"MTI-REVIEW\"\n");

            else
               fprintf(fout, "Citation is Designated as \"MTI-AUTO\"\n");
            fflush(fout);
        } /* fi */
    } /* fi */

    else
    {
        sprintf(cmd, "grep \"|%s|\" %s\0", inJID, FIRSTLINE_FILE);
        fp = popen(cmd, "r");

        if(fp != NULL)
        {
           while(fgets(line, MAXLINE, fp) != (char *)NULL)
           {
               if(line[0] != '#')  /* Maybe it has been deleted from program */
               {
                   isReview = -1;
                   strcpy(JID, "");
                   sscanf(line, "%*[^|]|%[^|]|%d|", JID, &isReview);
                   if(strlen(JID) > 0)
                   {
                       firstLineJournal = TRUE;
                       medFilter_set = TRUE;

                       if(isReview == 1)
                       {
                           firstLineReviewJournal = TRUE;
                           if(RTM_Debug)
                           {
                               fprintf(fout, "Journal is MTIFL Review: %s\n",
                                       JID);
                               fflush(fout);
                           } /* fi */
                       } /* fi */
                   } /* fi */
               } /* fi */
           } /* while */

           pclose(fp);
        } /* fi */

        else
        {
            fprintf(stderr, "ERROR - Unable to open cmd: #%s#\n", cmd);
            fflush(stderr);
        } /* else */
    } /* else !MTI_AUTO */
} /* checkFirstLine */

