/***************************************************************************
*
*  foundInTextXdOut --
*
*      This 
*
***************************************************************************/

int foundInTextXdOut(char *lookFor, char *endMH, int flag)
{
    int rtn, okLeft, ok, rOK, hasSpaceAEnd;
    long j, diff, gLen, rPos, pos, x;
    char *foo, *tmpCit, tmp[250];

    rtn = hasSpaceAEnd = FALSE;
    tmpCit = strdup(fITXObase);
    gLen = (long)strlen(lookFor);
    hasSpaceAEnd = doIHaveSpaceA_End(lookFor, gLen);

    /* Make sure we don't have one of the "non" versions of what we are looking for first */

    buildNonList(lookFor);
    for(x = 0; x < numNon; x++)
    {
        gLen = (long)strlen(nonList[x]);
        hasSpaceAEnd = doIHaveSpaceA_End(nonList[x], gLen);
        foo = strstr(tmpCit, nonList[x]);
        while(foo != NULL)
        {
            okLeft = ok = FALSE;
            diff = foo - tmpCit;
            if(diff == 0)
              okLeft = TRUE;

            else if(diff > 0)
            {
               if(isspace(tmpCit[diff - 1]) || ispunct(tmpCit[diff - 1]))
                 okLeft = TRUE;

               else if(lookFor[0] == ':')
                 okLeft = TRUE;
            } /* else fi */

            if(okLeft)
            {
                if((gLen > 5) && !hasSpaceAEnd)
                {
                    pos = diff + gLen;

                    if((pos >= fITXOlen) || ispunct(tmpCit[pos]) || 
                       isspace(tmpCit[pos]) || (tmpCit[pos] == 's'))
                    {
                        rOK = TRUE;

                       /* Need to check if really plural or just part of word -
                          E.g., don't want "mus" hitting on "mussels".

                          ALSO, don't want case like "needles" -> "needless" 091415
                       */

                       if(tmpCit[diff + gLen] == 's')
                       {
                           rOK = FALSE;
                           if(tmpCit[pos - 1] != 's') /* Don't want plural "ss" */
                           {
                               rPos = pos + 1;
                               if(rPos >= fITXOlen) /* Reached end */
                                 rOK = TRUE;
                               else
                               {
                                   if(isspace(tmpCit[rPos]) || ispunct(tmpCit[rPos]))
                                     rOK = TRUE;
                               } /* else */
                           } /* fi */
                       } /* fi plural */                

                       if(rOK)
                         ok = TRUE;
                    } /* fi */
                } /* fi */

                else
                {
                    if(((diff + gLen) >= fITXOlen) || 
                       ispunct(tmpCit[diff + gLen]) || isspace(tmpCit[diff + gLen]))
                      ok = TRUE;
                } /* else */
            } /* fi */

            for(j = 0; j < gLen; j++)
            {
                if(ok)  /* Permanent changes to text */
                {
                   tmpCit[j + diff] = 'Y';
                   if(flag)
                     fITXObase[j + diff] = 'Y';
                } /* fi */

                else  /* Temporary change to local copy of text */
                  tmpCit[j + diff] = 'X';
            } /* for */

            foo = strstr(tmpCit, nonList[x]);
        } /* while */
    } /* for each nonList item */

    /* clean out the "non" list freeing up memory */

    for(x = 0; x < numNon; x++)
      free(nonList[x]);
    numNon = 0;

    /* Ok, now that we have masked all of the "non" items, let's see if actually have the lookFor */

    gLen = (long)strlen(lookFor);
    hasSpaceAEnd = doIHaveSpaceA_End(lookFor, gLen);
    foo = strstr(tmpCit, lookFor);
    while(foo != NULL)
    {
        okLeft = ok = FALSE;
        diff = foo - tmpCit;
        if(diff == 0)
          okLeft = TRUE;

        else if(diff > 0)
        {
           if(isspace(tmpCit[diff - 1]) || ispunct(tmpCit[diff - 1]))
             okLeft = TRUE;

           else if(lookFor[0] == ':')
             okLeft = TRUE;
        } /* else fi */

        if(okLeft)
        {
            /* If gLen (length of lookFor) > 5, allow plural also.
               Added 4/10/14

              If we have a "<space>A" ending, don't allow plural
              e.g., isoplysin A
              Added 11/8/16
            */

            if((gLen > 5) && !hasSpaceAEnd)
            {
                pos = diff + gLen;

                if((pos >= fITXOlen) || ispunct(tmpCit[pos]) || 
                   isspace(tmpCit[pos]) || (tmpCit[pos] == 's'))
                {
                   rOK = TRUE;

                   /* Need to check if really plural or just part of word -
                      E.g., don't want "mus" hitting on "mussels".

                      ALSO, don't want case like "needles" -> "needless" 091415
                   */

                   if(tmpCit[diff + gLen] == 's')
                   {
                       rOK = FALSE;
                       if(tmpCit[pos - 1] != 's') /* Don't want plural "ss" */
                       {
                           rPos = pos + 1;
                           if(rPos >= fITXOlen) /* Reached end */
                             rOK = TRUE;
                           else
                           {
                               if(isspace(tmpCit[rPos]) ||
                                  ispunct(tmpCit[rPos]))
                                 rOK = TRUE;
                           } /* else */
                       } /* fi */
                   } /* fi plural */                

                   if(rOK)
                   {
                       ok = TRUE;
                       rtn = TRUE;
                   } /* fi */
                } /* fi */
            } /* fi */

            else
            {
                if(((diff + gLen) >= fITXOlen) || 
                   ispunct(tmpCit[diff + gLen]) || isspace(tmpCit[diff + gLen]))
                {
                    ok = TRUE;
                    rtn = TRUE;
                } /* fi */
            } /* else */
        } /* fi */

        /* Add to global ET & PI if ok */

        if(ok && trackPositional)
        {
            if(strlen(globalET) > 2)
              strcat(globalET, ",");
            sprintf(tmp, "\"%s\"-ti-0-\"\0", endMH);
            strcat(globalET, tmp);
            strcat(globalET, lookFor);
            strcat(globalET, "\"");

            if(strlen(globalPI) > 0)
              strcat(globalPI, ";");

            /* +6 for "TI  - " */

            sprintf(tmp, "%ld^%ld^0\0", diff + TIStartPos + 6, gLen);
            strcat(globalPI, tmp);
        } /* fi */

        for(j = 0; j < gLen; j++)
        {
            if(ok)  /* Permanent changes to text */
            {
               tmpCit[j + diff] = 'Y';
               if(flag)
                 fITXObase[j + diff] = 'Y';
            } /* fi */

            else  /* Temporary change to local copy of text */
              tmpCit[j + diff] = 'X';
        } /* for */

        foo = strstr(tmpCit, lookFor);
    } /* while */

    free(tmpCit);
    return(rtn);
} /* foundInTextXdOut */

