void processAntonio()
{
    char line[MAXLINE + 1], cmd[SMALL_LINE + 1], aMH[SMALL_LINE + 1];
    long i, index;
    int status, found, isHumans, isMale, isFemale, isSwine;
    FILE *fp;

    for(i = 0; i < NUM_ANTONIO_MHs; i++)
    {
        MLPicks.MHstatus[i] = FALSE;
        MLPicks.found[i] = FALSE;
    } /* for */

    sprintf(cmd, "%s < %s\0", ANTONIOCMD, tmp_input_Orig_file);
    fp = popen(cmd, "r");
    if(fp != NULL)
    {
       while(fgets(line, MAXLINE, fp) != (char *)NULL)
       {
           if(RTM_Debug)
           {
               fprintf(fout, "Antonio: %s", line);
               fflush(fout);
           } /* fi */

           strcpy(aMH, "");
           status = -1;
           sscanf(line, "%*[^|]|%[^|]|%d", aMH, &status);

           if((strlen(aMH) > 0) && (status > -1))
           {
               if(status)
               {
                   isHumans = isMale = isFemale = isSwine = FALSE;

                   if(strcmp(aMH, "Humans") == 0)
                     isHumans = TRUE;

                   if(strcmp(aMH, "Male") == 0)
                     isMale = TRUE;

                   if(strcmp(aMH, "Female") == 0)
                     isFemale = TRUE;

                   if(strcmp(aMH, "Swine") == 0)
                     isSwine = TRUE;

                   if(RTM_Debug)
                     fprintf(fout, "isMale: %d isFemale: %d  isHuman: %d\n",
                           isMale, isFemale, isHumans);

                   /***********************************************************
                       1) If Biography, no Humans - 7/12/13
                            -- Added Male & Female to Exclusion - 8/15/2013
  
                       2) NO HUMANS for Vet Journals

                       3) Nothing BUT Humans | Animals for MTIFL Review Journals

                       4) If Male and "her" in tiab, remove
                       5) If Female and "him" in tiab, remove

                       6) 2/24/14 For Title Only citations, ONLY allow Female,
                          Male, Humans, and Swine.
                   ***********************************************************/


                   if(isHumans == 0) /* #2 */
                   {
                       if(veterinaryJournal || insectsJournal || zootaxaJournal || botanyJournal)
                       {
                           status = FALSE;
                           if(RTM_Debug)
                           {
                               fprintf(fout,
                                    "Removing Antonio Humans - Vet Journal\n");
                               fflush(fout);
                           } /* fi */
                       } /* fi */
                   } /* fi */

                   else if(firstLineReviewJournal && !isHumans) /* #3 */
                   {
                       status = FALSE;
                       if(RTM_Debug)
                       {
                           fprintf(fout, "Removing Antonio non-Humans");
                           fprintf(fout, " (%s) - MTIFL Review\n", aMH);
                           fflush(fout);
                       } /* fi */
                   } /* else fi */

                   /* #6 */

                   if(status && Title_Only && ((isHumans != 0) &&
                       (isMale != 0) && (isFemale != 0) && (isSwine != 0)))
                   {
                       status = FALSE;
                       if(RTM_Debug)
                       {
                           fprintf(fout, "Removing Antonio Title Only");
                           fprintf(fout, " (%s) - MTIFL Review\n", aMH);
                           fflush(fout);
                       } /* fi */
                   } /* fi */

                   if(status && (strstr(PTs, "Biography") != NULL)) /* #1 */
                   {
                       if((isHumans == 0) || (isMale == 0) || (isFemale == 0))
                       {
                           status = FALSE;
                           if(RTM_Debug)
                           {
                               fprintf(fout, "Removing Antonio");
                               fprintf(fout, " (%s) - Biography\n", aMH);
                               fflush(fout);
                           } /* fi */
                       } /* fi */
                   } /* fi */

                   /* Filtering for Male & Female added 1/22/14 */

                   if(status && (isMale == 0))
                   {
                       if(foundInText("her", FALSE, FALSE))
                       {
                           status = FALSE;
                           if(RTM_Debug)
                           {
                               fprintf(fout, "Removing Antonio");
                               fprintf(fout, " (%s) - Her\n", aMH);
                               fflush(fout);
                           } /* fi */
                       } /* fi */
                   } /* fi */

                   if(status && (isFemale == 0))
                   {
                       if(foundInText("him", FALSE, FALSE))
                       {
                           status = FALSE;
                           if(RTM_Debug)
                           {
                               fprintf(fout, "Removing Antonio");
                               fprintf(fout, " (%s) - Him\n", aMH);
                               fflush(fout);
                           } /* fi */
                       } /* fi */
                   } /* fi */
               } /* fi status */

               found = FALSE;
               for(i = 0; !found && (i < NUM_ANTONIO_MHs); i++)
               {
                   if(aMH[0] == AntonioMHs[i][0])
                   {
                       if(strcmp(aMH, AntonioMHs[i]) == 0)
                       {
                           found = TRUE;
                           MLPicks.MHstatus[i] = status;
                       } /* fi */
                   } /* fi */
               } /* for */
           } /* fi */

           else
           {
               fprintf(stderr, "ERROR - Bad Antonio Line: #%s#\n", line);
               fflush(stderr);
           } /* else */
       } /* while */

       pclose(fp);
    } /* fi */

    else
    {
        fprintf(stderr, "ERROR - Unable to open file: #%s#\n", cmd);
        fflush(stderr);
    } /* else */
} /* processAntonio */


/***************************************************************************
*
*  hasTUI --
*
*      This 
*
***************************************************************************/

int hasTUI(char *checkMH)
{
    int rtn, n, z;
    long i, len;
    char *tmp;
    char **rows = NULL;

    rtn = FALSE;
    len = (long)strlen(checkMH);
    tmp = strdup(checkMH);
    for(i = 0; i < len; i++)
    {
        if(isupper(tmp[i]))
         tmp[i] = tolower(tmp[i]);
    } /* for */

    query_btree(MHTUI, tmp, &rows, &n);
    if((rows != NULL) && (n > 0))
    {
       rtn = TRUE;
       for(z = 0; z < n; z++)
       {
           if(rows[z] != NULL)
             free(rows[z]);
       } /* for */
    } /* fi */

    if(rows != NULL)
      free(rows);

    free(tmp);
    return(rtn);
} /* hasTUI */

