/***************************************************************************
*
*  pull_CatalogPRC --
*
*      This
*   .
***************************************************************************/

void *pull_CatalogPRC()
{
    int isIM, done;
    long x, PMID, numP, numMH, count, i;
    float score;
    char mh[SMALL_LINE + 1], tmp[MED_LINE + 1], rest[MED_LINE + 1],
         triple[SMALL_LINE + 1], shList[MED_LINE + 1], *foo,
         Pline[250][MED_LINE + 1], tmp2[SMALL_LINE + 1], sh[SMALL_LINE + 1],
         NLMID[SMALL_LINE + 1];

    /* Setup PRC_PMIDs array using the lines from CRC - 1 line per CRC */

    numP = 0;
    for(x = 0; x < 250; x++)
      strcpy(Pline[x], "");

    /* This whole section is to reformat each line from CRC to conform
       to what we have in the localPubMed lines for the PRC information
       so we can reuse the same routine to finish processing both.

       NOTE: Not all MeSH Terms coming from the CRC will be actual
         MeSH terms apparently (11/14/2013).  NLMID: 9205351 has the
         following entry: MeSH: Spa (Belgium).  This is not a valid MeSH
         term.
    */

    for(x = 0; x < numPl; x++)
    {
        if(PRC_Debug)
        {
            fprintf(fout, "PRClines[%ld]: #%s#\n", x, PRClines[x]);
            fflush(fout);
        } /* fi */

        if(isdigit(PRClines[x][0]))
        {
            PMID = -1;
            score = -1.0;
            strcpy(tmp, "");
            strcpy(NLMID, "");
            sscanf(PRClines[x], "%*ld|%f|%ld|%[^|]|%[^\n]", &score, &PMID,
                   NLMID, tmp);
            if((score > -1.0) && (PMID > -1) && (strlen(tmp) > 0) &&
               (numP < 250) && (strlen(NLMID) > 0))
            {
                numMH = 0;
/*
                sprintf(Pline[numP], "%ld|1|\0", PMID);
                sprintf(Pline[numP], "%ld+%s|1|\0", PMID, NLMID);
*/
                sprintf(Pline[numP], "%ld+%s|1|\0", PMID, NLMID);
                if(numPRC < MAX_PRC)
                {
/*
                    sprintf(tmp2, "%ld\0", PMID);
                    sprintf(tmp2, "%ld+%s\0", PMID, NLMID);
*/
                    sprintf(tmp2, "%ld+%s\0", PMID, NLMID);
                    PRC_PMIDs[numPRC] = strdup(tmp2);
                    PRC_Scores[numPRC] = score;
                    numPRC++;
                } /* fi */

                while(strlen(tmp) > 0)
                {
                    strcpy(triple, "");
                    strcpy(rest, "");
                    sscanf(tmp, "%[^:]:%[^\n]", triple, rest);
                    strcpy(tmp, rest);

                    isIM = FALSE;
                    if(strstr(triple, "^IM") != NULL)
                      isIM = TRUE;
                    strcpy(mh, "");
                    strcpy(shList, "");
                    sscanf(triple, "%[^^]^%*[^^]^%[^\n]", mh, shList);
                    if(strlen(mh) > 0)
                    {
                        foo = findMHDUI(mh);

                        if(strlen(foo) > 0)
                        {
                            sprintf(tmp2, "%s^%d\0", foo, isIM);
                            if(numMH > 0)
                              strcat(Pline[numP], ":");
                            strcat(Pline[numP], tmp2);
                        } /* fi */

                        else if(RTM_Debug || PRC_Debug)
                        {
                            fprintf(stderr, "ERROR: MH/DUI Not Found -");
                            fprintf(stderr, "%ld|%s\n", PMID, mh);
                            fflush(stderr);
                        } /* else */
                        free(foo);

                        while(strlen(shList) > 0)
                        {
                            strcpy(sh, "");
                            strcpy(rest, "");
                            sscanf(shList, "%[^/]/%[^\n]", sh, rest);
                            strcpy(shList, rest);

                            if(strlen(sh) > 0)
                            {
                                foo = findSHQUI(sh);
                                if(strlen(foo) > 0)
                                {
                                    strcat(Pline[numP], "^");
                                    strcat(Pline[numP], foo);
                                } /* fi */

                                else if(RTM_Debug || PRC_Debug)
                                {
                                   fprintf(stderr, "ERROR: SH/QUI Not Found -");
                                   fprintf(stderr, "%ld|%s\n", PMID, sh);
                                   fflush(stderr);
                                } /* else */
                                free(foo);
                            } /* fi */

                            else if(RTM_Debug || PRC_Debug)
                            {
                                fprintf(stderr, "ERROR: Bad Cat SH: #%s#\n",
                                        triple);
                                fflush(stderr);
                            } /* else */
                        } /* while */

                        numMH++;
                    } /* fi */

                    else if(RTM_Debug || PRC_Debug)
                    {
                        fprintf(stderr, "ERROR: Malformed CAT Triple: #%s#\n",
                                triple);
                        fflush(stderr);
                    } /* else */
                } /* while */

                strcat(Pline[numP], "|01010101");
                if(RTM_Debug || PRC_Debug)
                {
                    fprintf(fout, "Pline[%d]: %s\n", numP, Pline[numP]);
                    fflush(fout);
                } /* fi */
                numP++;
            } /* fi valid PRCline */

            else if((numP < 250) && (RTM_Debug || PRC_Debug))
            {
                fprintf(stderr, "ERROR: Bad PRCLines[%ld]: #%s#\n", x,
                        PRClines[x]);
                fflush(stderr);
            } /* else fi */
        } /* fi isdigit */
    } /* for each PRCline */

    /* Now handle CRC information */

    count = 0;
    done = FALSE;
    for(i = 0; !done && (i < numP); i++)
    {
        process_PRC(Pline[i], count, i);
        count++;
        if(count >= top_relcit)
          done = TRUE;
    } /* for */

    /* Just clean up the list */

    for(x = 0; x < numP; x++)
      strcpy(Pline[x], "");
} /* pull_CatalogPRC */

/***************************************************************************
*
*  foundInAAText --
*
*      This 
*
***************************************************************************/

int foundInAAText(char *lookFor)
{
   int rtn = FALSE;
   char *base, *foo, *baseLookFor;
   int okLeft, okRight, rOK;
   long i, baseLen, lfLen, diff, rPos;

   okLeft = FALSE;
   okRight = FALSE;
   baseLen = 0;
   lfLen = (long)strlen(lookFor);

   base = strdup(AA_cit);
   baseLen = AACitLen;
   baseLookFor = strdup(lookFor);

   foo = strstr(base, baseLookFor);
   while(!rtn && (foo != NULL))
   {
       okLeft = okRight = FALSE;
       diff = foo - base;
       if(diff == 0)
          okLeft = TRUE;

       else if(diff > 0)
       {
          if(isspace(base[diff - 1]) || ispunct(base[diff - 1]))
            okLeft = TRUE;
       } /* else fi */

       if(okLeft)
       {
          if(RTM_Debug)
          {
             fprintf(fout, "foundInAAText: Left-side ok (%s)\n", baseLookFor);
             fflush(fout);
          } /* fi */

          if(((diff + lfLen) >= baseLen) || isspace(base[diff + lfLen]) ||
            ispunct(base[diff + lfLen]) || (base[diff + lfLen] == 's'))
          {
              rOK = TRUE;

              /* Need to check if really plural or just part of word -
                 E.g., don't want "mus" hitting on "mussels". 010411 - JGM
              */

              if(base[diff + lfLen] == 's')
              {
                  rOK = FALSE;
                  rPos = diff + lfLen + 1;
                  if(rPos >= baseLen) /* Reached end */
                    rOK = TRUE;
                  else
                  {
                      if(isspace(base[rPos]) || ispunct(base[rPos]))
                        rOK = TRUE;
                  } /* else */
              } /* fi */                

              if(rOK)
              {
                 okRight = TRUE;
                 if(RTM_Debug)
                 {
                    fprintf(fout, "foundInAAText: Right-side ok\n");
                    fflush(fout);
                 } /* fi */
              } /* fi rOK */
          } /* fi diff, space, or punct right */
       } /* fi okLeft */

       if(okLeft && okRight)
         rtn = TRUE;
 
       strcpy(base, foo + lfLen);
       foo = strstr(base, baseLookFor);
  } /* while foo */

   free(base); free(baseLookFor);
   return(rtn);
} /* foundInAAText */

