long checkAAs(char *lookFor)
{
   int foundAA;
   long rtn, j;

   rtn = -1;
   foundAA = FALSE;
   for(j = 0; !foundAA && (j < numAAs); j++)
   {
       if(strcasecmp(lookFor, AAs[j].lookFor) == 0)
       {
           foundAA = TRUE;
           rtn = j;
       } /* for */
   } /* for */

   if(foundAA)
   {
       if(foundInAAText(lookFor))
         rtn = -1;
   } /* fi */

   return(rtn);
} /* checkAAs */

