/***************************************************************************
*
*  checkEntryTerm --
*
*      This 
*
***************************************************************************/

int checkEntryTerm(char *ET)
{
    int rtn, done, exception, check;
    long len, i, AApos;
    char term[SMALL_LINE + 1], tmp[SMALL_LINE + 1], rest[SMALL_LINE + 1];

    /* Exceptions: Commonly used (and known) AAs
       NMR - Magnetic Resonance Spectroscopy
       LPS - Lipopolysaccharides
       HLA - HLA Antigens
       HPLC - Chromatography, High Pressure Liquid
       TNM - TNM Staging -> Neoplasm Staging
       HIV - HIV Infections
       PCR - Polymerase Chain Reaction
       DNA - Deoxyribonucleotide
       MRI - Magnetic Resonance Imaging
       GFP - Green Fluorescent Proteins
       PTEN - PTEN Phosphohydrolase
       RFLP - Polymorphism, Restriction Fragment Length
       SNP - Polymorphism, Single Nucleotide
       IRF - Interferon Regulatory Factors
       MAP - Mitogen-Activated Protein
       TURP - Transurethral Resection of Prostate

       MMP# - Matrix Metalloproteinase
       ERK# - Mitogen-Activated Protein Kinase
       CD# -  Antigens, CD
       BMP# - Bone Morphogenetic Protein
       CDK# - Cyclin-Dependent Kinase
       IGF# - Insulin-Like Growth Factor
       JAK# - Janus Kinase
       IRF# - Interferon Regulatory Factor

       RNA - Ribonucleic Acid
    */

    rtn = TRUE;
    strcpy(tmp, ET);

    done = exception = FALSE;
    while(!done && !exception && (strlen(tmp) > 0))
    {
        strcpy(term, "");
        strcpy(rest, "");
        sscanf(tmp, "%*[^\"]\"%*[^\"]\"%*[^\"]\"%[^\"]\"%[^\n]", term, rest);
        strcpy(tmp, rest);
        len = (long)strlen(term);

        if(len > 4)
          done = TRUE;

        else
        {
            check = TRUE;
            if((strcmp(term, "NMR") == 0)  || (strcmp(term, "LPS") == 0) ||
               (strcmp(term, "HLA") == 0)  || (strcmp(term, "HPLC") == 0) ||
               (strcmp(term, "TNM") == 0)  || (strcmp(term, "HIV") == 0) ||
               (strcmp(term, "PCR") == 0)  || (strcmp(term, "DNA") == 0) ||
               (strcmp(term, "MRI") == 0)  || (strcmp(term, "GFP") == 0) ||
               (strcmp(term, "PTEN") == 0) || (strcmp(term, "RFLP") == 0) ||
               (strcmp(term, "SNP") == 0)  || (strcmp(term, "IRF") == 0) ||
               (strcmp(term, "MAP") == 0)  || (strcmp(term, "RNA") == 0) ||
               (strcmp(term, "TURP") == 0))
            {
                exception = TRUE;
                check = FALSE;
            } /* fi */

            /* These, we need to see if last char is a digit or not */

            if((len > 0) && check)
            {
                if((strncmp(term, "MMP", 3) == 0) ||
                   (strncmp(term, "ERK", 3) == 0) ||
                   (strncmp(term, "CD", 2) == 0) ||
                   (strncmp(term, "BMP", 3) == 0) ||
                   (strncmp(term, "CDK", 3) == 0) ||
                   (strncmp(term, "IGF", 3) == 0) ||
                   (strncmp(term, "JAK", 3) == 0) ||
                   (strncmp(term, "IRF", 3) == 0))
                {
                    if(isdigit(term[len - 1]))
                    {
                        exception = TRUE;
                        check = FALSE;
                    } /* fi */
                } /* fi */
            } /* fi */

            /* Not an exception, so see if we should remove it or not */

            if((len > 0) && check)
            {
                for(i = 0; !done && (i < len); i++)
                {
                    if(!isupper(term[i]))
                    {
                        if(!isdigit(term[i]))
                          done = TRUE;
                    } /* fi */
                } /* for */
            } /* fi */

            /* If still want to remove, see if author defined AA or not
               Added exception 4/4/14
            */

            if(!done && !exception)
            {
                AApos = isAA(term);
                if(AApos > -1)
                  exception = TRUE;
            } /* fi */
        } /* else */

        if(RTM_Debug)
        {
            fprintf(fout, "   ET: %d|%ld|%s|\n", done, len, term);
            fflush(fout);
        } /* fi */
    } /* while */

    if(!done && !exception)
      rtn = FALSE;

    return(rtn);
} /* checkEntryTerm */

/***************************************************************************
*
*  checkVocabDensity --
*
*      This 
*
***************************************************************************/

void checkVocabDensity()
{
    int done, doVocabDen_Check, found, remove;
    long i, j, numArticles, z;
    float VocabDenFactor;
    char DUI[25], mh[SMALL_LINE + 1], *foo;

    if(RTM_Debug)
      fprintf(fout, "checkVocabDensity:\n");

    /* Review MHs (only) to see if we have any Vocabulary Density information
       for the MH.  We are not going to remove any Forced MHs, so don't need
       review them.

       If we don't find any results from our first check, no need to check
       the rest because we won't have information for the journal.
    */

    done = FALSE;

    /* Check journal with dummy entry to see if we need to continue */

    foo = getVocabDen(nlmID, "D000000");
    if(strlen(foo) > 0)
    {
        numArticles = -1;
        sscanf(foo, "%*[^|]|%*[^|]|%*[^|]|%ld", &numArticles);

        /* 1/7/14 - Added cut-off of 80 */

        if(numArticles < 81)
        {
            done = TRUE;
            if(RTM_Debug)
            {
                fprintf(fout, "VocabDen check stopped - not enough articles:");
                fprintf(fout, " %ld|%s|\n", numArticles, nlmID);
                fflush(fout);
            } /* fi */
        } /* fi */
    } /* fi */

    else
    {
        done = TRUE;
        if(RTM_Debug)
        {
            fprintf(fout,
               "VocabDen check stopped - no entries for journal (%s)\n", nlmID);
            fflush(fout);
        } /* fi */
    } /* else */

    free(foo);

    /* Removed exceptions for forced lookup in AB since it added
       way more wrong than correct answers. 9/15/16

        else if(mt_table[i].oktoprint && (mt_table[i].datatype == MH) &&
           (strstr(mt_table[i].trigger, "Lookup AB")!= NULL))
           doVocabDen_Check = FALSE;
    */

    for(i = 0; !done && (i < mt_count); i++)
    {
        doVocabDen_Check = FALSE;

        if(mt_table[i].oktoprint && (mt_table[i].datatype == MH) &&
           mt_table[i].forcePrint)
            mt_table[i].VocabDenFactor = 1.4;  /* Indicates forced */

        else if(mt_table[i].oktoprint && (mt_table[i].datatype == MH) &&
           !mt_table[i].forcePrint)
          doVocabDen_Check = TRUE;

        /* Check CTs added 1/7/14
           Updated 4/29/15 to allow if from MTI (found in text).
           Updated 4/30/15 to do check for age related tags if from MMI 
         */

        else if(mt_table[i].oktoprint && (mt_table[i].datatype == CT))
        {
            if(!mt_table[i].paths[MMI])
              doVocabDen_Check = TRUE;
            else
            {
                if((strcmp(mt_table[i].mh, "Adolescent") == 0) ||
                   (strcmp(mt_table[i].mh, "Adult") == 0) ||
                   (strcmp(mt_table[i].mh, "Aged") == 0) ||
                   (strcmp(mt_table[i].mh, "Aged, 80 and over") == 0) ||
                   (strcmp(mt_table[i].mh, "Child, Preschool") == 0) ||
                   (strcmp(mt_table[i].mh, "Infant") == 0) ||
                   (strcmp(mt_table[i].mh, "Middle Aged") == 0) ||
                   (strcmp(mt_table[i].mh, "Young Adult") == 0))
                  doVocabDen_Check = TRUE;
                else
                  doVocabDen_Check = FALSE;
            } /* else */
        } /* else fi */

        /* Protect Geographical terms that were found in article -
           Tested and Removed 29 July 2015.  Increased recall 0.04
           but, dropped precision 0.09.
        */
/*
        if(doVocabDen_Check)
        {
            if(amIGeographical(i) && mt_table[i].paths[MMI])
              doVocabDen_Check = FALSE;
        }
*/

        /* Protect New Terms, even if from AB -- If PRC only, need at least 4 */

        if(doVocabDen_Check)
        {
            if(binsearchNewCheck(mt_table[i].mh, mt_table[i].dui))
            {
                if((!mt_table[i].paths[MMI] && mt_table[i].paths[PRC] &&
                   (mt_table[i].num_RCs > 3)) || mt_table[i].paths[MMI])
                {
                    doVocabDen_Check = FALSE;
                    mt_table[i].VocabDenFactor = 1.1;  /* Indicates forced */
                } /* fi */
            } /* fi */
        } /* fi */

        /* Protect NonLeaf Terms, if not necessary, they are removed later */

        if(doVocabDen_Check)
        {
            found = FALSE;
            for(z = 0; !found && (z < numNL); z++)
            {
                if(strcmp(mt_table[i].dui, nonLeafs[z].DUI) == 0)
                  found = TRUE;
            } /* for */

            if(found)
            {
                doVocabDen_Check = FALSE;
                mt_table[i].VocabDenFactor = 1.2;  /* Indicates forced */
            } /* fi */
        } /* fi */

        /* If both MMI & PRC (4 or more) recommend, also save */

        if(doVocabDen_Check)
        {
            if(mt_table[i].paths[MMI] && mt_table[i].paths[PRC] &&
               (mt_table[i].num_RCs > 3))
            {
                doVocabDen_Check = FALSE;
                mt_table[i].VocabDenFactor = 1.3;  /* Indicates forced */
            } /* fi */
        } /* fi */

        if(RTM_Debug)
        {
            if(doVocabDen_Check)
              fprintf(fout, "Vocab - MH: %s doVocabDen_Check: %d  ", 
                      mt_table[i].mh, doVocabDen_Check);
            else
              fprintf(fout, "Vocab - Ignore MH: %s doVocabDen_Check: %d  ", 
                      mt_table[i].mh, doVocabDen_Check);

            fprintf(fout, "MMI: %d  PRC: %d  numPRC: %ld\n", 
                  mt_table[i].paths[MMI], mt_table[i].paths[PRC],
                  mt_table[i].num_RCs);
            fflush(fout);
        } /* fi */

        if(doVocabDen_Check)
        {
            if(strstr(mt_table[i].trigger, "Entry Term Replacement") != NULL)
              strcpy(mh, mt_table[i].mh_orig);

            else
              strcpy(mh, mt_table[i].mh);

            if(RTM_Debug)
            {
                fprintf(fout, "VocabDen - Checking: %s|%s|%s|%s|\n", nlmID,
                        mt_table[i].dui, mh, mt_table[i].trigger);
                fflush(fout);
            } /* fi */

            foo = getVocabDen(nlmID, mt_table[i].dui);

            /* if section is here in case we want to do anything with usage
               factor (percentage).  Right now, it is yes or no, but, we
               may want to factor in the percentages.
            */

            if(strlen(foo) > 0)
            {
                VocabDenFactor = -1.0;
                sscanf(foo, "%*[^|]|%*[^|]|%*[^|]|%*[^|]|%f", &VocabDenFactor);
                mt_table[i].VocabDenFactor = VocabDenFactor;

                if(RTM_Debug)
                {
                    fprintf(fout, "VocabDen foo (%0.4f): #%s#\n",
                            VocabDenFactor, foo);
                    fflush(fout);
                } /* fi */
            } /* fi foo */

            else
            {
                if(RTM_Debug)
                {
                    fprintf(fout, "   -- Removing MH '%s' Vocab Density\n", mh);
                    fprintf(fout, "MM: %d  PRC: %d|%ld|loc: %s|%s|\n",
                            mt_table[i].paths[MMI], mt_table[i].paths[PRC],
                            mt_table[i].num_RCs, mt_table[i].textloc,
                            mt_table[i].trigger);
                    fflush(fout);
                } /* fi */

                if(RTM_Debug)
                  fprintf(fout, "VOCAB|%s|%s|\n", muid, mh);
                mt_table[i].oktoprint = FALSE;
                mt_table[i].safe = FALSE;
                mt_table[i].forcePrint = FALSE;
                mt_table[i].score = 0;
            } /* else */

            free(foo);
        } /* fi oktoprint, etc. */
    } /* for each mt_table entry */

    /* Now, look at adding terms that appear in 75% or more ofthe articles */

    if(!done) /* Found entries for this journal */
      addVocabTops();

    /* Added in Special Journal Triggers based on Susan's emails 2/24/14 */

    addSpecJournal();
} /* checkVocabDensity */

