char *getVocabDen(char *nlmID, char *DUI)
{
    int n, z;
    char **rows = NULL;
    char *rtn, tmp[SMALL_LINE + 1];

    rtn = (char *)malloc(5);
    strcpy(rtn, "");

    sprintf(tmp, "%s|%s\0", nlmID, DUI);
    query_btree(VOCABDEN, tmp, &rows, &n);
    if((rows != NULL) && (n > 0))
    {
        free(rtn);
        rtn = strdup(rows[0]);
        for(z = 0; z < n; z++)
        {
           if(rows[z] != NULL)
             free(rows[z]);
        } /* for */
    } /* fi */

    if(rows != NULL)
      free(rows);

    return(rtn);
} /* getVocabDen */

/***************************************************************************
*
*  addVocabTops --
*
*      This 
*
***************************************************************************/

void addVocabTops()
{
    int n, z, nD, zD, ok;
    long count, setSize, index;
    float factor, CTcheck;
    char **rows = NULL;
    char **rowsD = NULL;
    char mh[SMALL_LINE + 1], DUI[50], CUI[50];

    query_btree(VOCABTOP, nlmID, &rows, &n);
    if((rows != NULL) && (n > 0))
    {
        for(z = 0; z < n; z++)
        {
           factor = -1.0;
           count = setSize = -1;
           strcpy(DUI, "");
           strcpy(CUI, "");
           strcpy(mh, "");
           sscanf(rows[z], "%*[^|]|%[^|]|%ld|%ld|%f", DUI, &count,
                  &setSize, &factor);

           if(strlen(DUI) > 0)
           {
               query_btree(DUICUIMH, DUI, &rowsD, &nD);
               if((rowsD != NULL) && (nD > 0))
               {
                   sscanf(rowsD[0], "%*[^|]|%[^|]|%[^\n]", CUI, mh);
                   for(zD = 0; zD < nD; zD++)
                   {
                       if(rowsD[zD] != NULL)
                         free(rowsD[zD]);
                   } /* for */
               } /* fi */

               if(rowsD != NULL)
                 free(rowsD);

               if((strlen(CUI) > 0) && (strlen(mh) > 0))
               {
                   ok = TRUE;

                   /* Only want 100% CTs - 1/8/14 */
                   /* Allowing less 100% if difference is 5 or less
                       E.g., 9423848|D006801|1259|1260|0.999206|30|Humans
                      2/19/14
                   */

                   if(isCT(DUI))
                   {
/*
                       if((factor > 0.8499) || ((setSize - count) < 5))
                       if((factor > 0.7999) || ((setSize - count) < 5))
                       if((factor == 1.0) || ((setSize - count) < 5))

                      Moved to 80% 11/1315 after testing various levels.
                      Keeping 1.0 for MTIFL & CHJ journals though
                      Allowing 90% for BioASQ runs
                      Although statistically 90% for MTIFL/CHJ works better,
                      it provides too many bad CTs for the indexers.  So,
                      just using it for the BioASQ runs for now.
*/
                       CTcheck = 1.0;

                       if(goHigh)
                         CTcheck = 0.6999;

                       else if(BioASQ)
                         CTcheck = 0.8999;

                       else if(firstLineJournal || MTI_AUTO)
                         CTcheck = 1.0;

                       else
                         CTcheck = 0.7999;

                       if((factor > CTcheck) || ((setSize - count) < 5))
                       {
                           process_checktags(mh, MMI, DUI,
                                     "Forced - Vocab Density",
                                     "Forced - Vocab Density");

                           index = search_index(DUI);
                           if(index >= 0)
                           {
                               mt_table[index].score += 1000;
                               mt_table[index].oktoprint = TRUE;
                               mt_table[index].forcePrint = TRUE;
                           } /* fi */
                       } /* fi */

                       else
                         ok = FALSE;
                   } /* fi */

                   else
                   {
                       process_mterm(FALSE, "", mh, MMI, 1000.0, MH, "", "",
                            "Forced - Vocab Density", DUI, "", "", TRUE);

                       index = search_index(DUI);
                       if(index >= 0)
                       {
                           mt_table[index].score += 1000;
                           mt_table[index].oktoprint = TRUE;
                           mt_table[index].forcePrint = TRUE;
                       } /* fi */
                   } /* else */

                   if(ok && RTM_Debug)
                   {
                      fprintf(fout, "Adding: \"%s\" -- Vocab Density\n", mh);
                      fflush(fout);
                   } /* if */
               } /* fi */
           } /* fi */

           if(rows[z] != NULL)
             free(rows[z]);
        } /* for */
    } /* fi */

    if(rows != NULL)
      free(rows);
} /* addVocabTops */

