/***************************************************************************
*
*  findNonInText --
*
*      This 
*
***************************************************************************/

void findNonInText()
{
    int okLeft, ok, rOK;
    long j, diff, gLen, rPos, len;
    char *foo, *tmpCit, tmp[250];

    tmpCit = (char *)malloc(gTLen + gALen + 1000);
    sprintf(tmpCit, "PMID- %s\nTI  - \0", main_uid);
    strcat(tmpCit, globalTitle);
    if(gALen > 0)
    {
       strcat(tmpCit, "\nAB  - ");
       strcat(tmpCit, globalAbstract);
    } /* fi */
    len = (long)strlen(tmpCit);

    gLen = 4;
    foo = strstr(tmpCit, "non-");
    if(foo == NULL)
      foo = strstr(tmpCit, "non ");

    while(foo != NULL)
    {
        okLeft = ok = FALSE;
        diff = foo - tmpCit;
        if(diff == 0)
          okLeft = TRUE;

        else if(diff > 0)
        {
           if(isspace(tmpCit[diff - 1]))
             okLeft = TRUE;

           if(RTM_Debug)
           {
               fprintf(fout, "findNonInText - okLeft\n");
               fflush(fout);
           } /* fi */
        } /* else fi */

        if(okLeft)
        {
            if(((diff + gLen) >= len) || isalpha(tmpCit[diff + gLen]))
            {

               if(RTM_Debug)
               {
                   fprintf(fout, "findNonInText - okRight\n");
                   fflush(fout);
               } /* fi */

               ok = TRUE;
            } /* fi */
        } /* fi */

        /* Add to global ET & PI if ok */

        if(ok && (num_nPI < 100))
          nonPIs[num_nPI++] = diff;

        for(j = 0; j < gLen; j++)
          tmpCit[j + diff] = 'X';

        foo = strstr(tmpCit, "non-");
        if(foo == NULL)
          foo = strstr(tmpCit, "non ");
    } /* while */

    free(tmpCit);
} /* findNonInText */

/***************************************************************************
*
*  check_nonPI --
*
*      This returns FALSE if "non" found within range of concept
*  Handles cases where we have both diabetic and non-diabetic via counts
*
***************************************************************************/

int check_nonPI(char *lPI, char *MMI_term)
{
    int rtn;
    long i, start, len, end, t, negCount, totalPICount;
    char tmp[SMALL_LINE + 1], PIs[SMALL_LINE + 1], trest[SMALL_LINE + 1], 
         rest[SMALL_LINE + 1];

    negCount = totalPICount = 0;

/*  Don't need with first 3 test below
       (strncmp(MMI_term, "non-", 4) != 0) &&
       (strncmp(MMI_term, "Non-", 4) != 0) &&
       (strncmp(MMI_term, "non ", 4) != 0) &&
       (strncmp(MMI_term, "Non ", 4) != 0) &&
 
*/
    rtn = TRUE;
    if((strstr(MMI_term, "non-") == NULL) &&
       (strstr(MMI_term, "Non-") == NULL) &&
       (strncmp(MMI_term, "non", 3) != 0) &&
       (strncmp(MMI_term, "Non", 3) != 0))
    {
        if(num_nPI > 0)
        {
            strcpy(tmp, lPI);
            while(strlen(tmp) > 0)
            {
                strcpy(rest, "");
                strcpy(PIs, "");
                sscanf(tmp, "%[^;];%[^\n]", PIs, rest);
                strcpy(tmp, rest);

                if(strlen(PIs) > 0)
                {
                    while(strlen(PIs) > 0)
                    {
                        strcpy(trest, "");
                        start = len = -1;
                        sscanf(PIs, "%ld/%ld^%[^\n]", &start, &len, trest);
                        strcpy(PIs, trest);
                        if((start > -1) && (len > -1))
                        {
                            totalPICount++;
                            end = start + len;
                            for(i = 0; i < num_nPI; i++)
                            {
                                t = nonPIs[i] + 4;
                                if((t >= start) && (t <= end))
                                {
                                    negCount++;
                                    if(RTM_Debug)
                                    {
                                        fprintf(fout, 
                                        "range match (%s): %ld <= %ld <= %ld\n",
                                        MMI_term, start, t, end);
                                        fflush(fout);
                                    } /* fi */
                                } /* fi */
                            } /* for */
                        } /* fi */
                    } /* while */
                } /* fi */

                else
                {
                    fprintf(stderr, "ERR - Bad PI (%s): #%s#\n", muid, lPI);
                    fflush(stderr);
                } /* else */
            } /* while */
        } /* fi num_nPI */
    } /* fi doesn't contain "non" */

    if(RTM_Debug && (negCount > 0))
    {
        fprintf(fout, "%s - #%s# -- totalPIs: %ld  negCount: %ld\n", MMI_term,
                lPI, totalPICount, negCount);
        fflush(fout);
    } /* fi */

    if((totalPICount > 0) && (negCount == totalPICount))
      rtn = FALSE;

    return(rtn);
} /* check_nonPI */

