/***************************************************************************
*
*  havePlants --
*
*      This returns TRUE if we have any MH within the Plants Tree in the topn
*   current recommendations.  Otherwise, returns FALSE.
*
***************************************************************************/

int havePlants()
{
    int rtn;
    long i, k, ctr;

    ctr = 0;
    rtn = FALSE;
    for(i = 0; ~!rtn && (i < mt_count) && (ctr < topn); i++)
    {
        if(mt_table[i].oktoprint && (mt_table[i].datatype == MH))
        {
            ctr++;
            for(k = 0; k < mt_table[i].num_treecodes; k++)
            {
                if(mt_table[i].treecodes[k][0] == 'B')
                {
                    if(strstr(mt_table[i].treecodes[k], "B01.650") != NULL)
                      rtn = TRUE;
                } /* else fi "B" */
            } /* for each treecode */
       } /* fi valid recommendation */
    } /* for each recommendation */

    return(rtn);
} /* havePlants */

/***************************************************************************
*
*  doIHaveSpaceA_End --
*
*      Isarfelin A
*
***************************************************************************/

int doIHaveSpaceA_End(char *lookFor, long len)
{
    int rtn;
    char ending[5];

    rtn = FALSE;
    strcpy(ending, "");
    if(len > 2)
    {
        ending[2] = '\0';
        ending[1] = lookFor[len - 1];
        ending[0] = lookFor[len - 2];

        if(strcmp(ending, " a") == 0)
          rtn = TRUE;
    } /* fi */

    return(rtn);
} /* doIHaveSpaceA_End */

/***************************************************************************
*
*  foundInALLTextXdOut --
*
*      This 
*
***************************************************************************/

int foundInALLTextXdOut(char *lookFor)
{
    int rtn, okLeft, ok, rOK, hasSpaceAEnd;
    long j, diff, gLen, rPos, pos;
    char *foo, *tmpCit, tmp[250];

    rtn = hasSpaceAEnd = FALSE;
    tmpCit = strdup(fITXALLbase);
    gLen = (long)strlen(lookFor);
    hasSpaceAEnd = doIHaveSpaceA_End(lookFor, gLen);

    foo = strstr(tmpCit, lookFor);
    while(foo != NULL)
    {
        okLeft = ok = FALSE;
        diff = foo - tmpCit;
        if(diff == 0)
          okLeft = TRUE;

        else if(diff > 0)
        {
           if(isspace(tmpCit[diff - 1]) || ispunct(tmpCit[diff - 1]))
             okLeft = TRUE;
        } /* else fi */

        if(okLeft)
        {
            /* If gLen (length of lookFor) > 5, allow plural also.
               Added 4/10/14

               1/9/2018 - modified to > 3

              If we have a "<space>A" ending, don't allow plural
              e.g., isoplysin A
              Added 11/8/16
            */

            if((gLen > 3) && !hasSpaceAEnd)
            {
                pos = diff + gLen;

                if((pos >= fITXALLlen) || ispunct(tmpCit[pos]) || 
                   isspace(tmpCit[pos]) || (tmpCit[pos] == 's'))
                {
                   rOK = TRUE;

                   /* Need to check if really plural or just part of word -
                      E.g., don't want "mus" hitting on "mussels".

                      ALSO, don't want case like "needles" -> "needless" 091415
                   */

                   if(tmpCit[diff + gLen] == 's')
                   {
                       rOK = FALSE;
                       if(tmpCit[pos - 1] != 's') /* Don't want plural "ss" */
                       {
                           rPos = pos + 1;
                           if(rPos >= fITXALLlen) /* Reached end */
                             rOK = TRUE;
                           else
                           {
                               if(isspace(tmpCit[rPos]) ||
                                  ispunct(tmpCit[rPos]))
                                 rOK = TRUE;
                           } /* else */
                       } /* fi */
                   } /* fi plural */                

                   if(rOK)
                   {
                       ok = TRUE;
                       rtn = TRUE;
                   } /* fi */
                } /* fi */
            } /* fi */

            else
            {
                if(((diff + gLen) >= fITXALLlen) || 
                   ispunct(tmpCit[diff + gLen]) || isspace(tmpCit[diff + gLen]))
                {
                    ok = TRUE;
                    rtn = TRUE;
                } /* fi */
            } /* else */
        } /* fi */

        for(j = 0; j < gLen; j++)
        {
            if(ok)  /* Permanent changes to text */
            {
               tmpCit[j + diff] = 'Y';
               fITXALLbase[j + diff] = 'Y';
            } /* fi */

            else  /* Temporary change to local copy of text */
              tmpCit[j + diff] = 'X';
        } /* for */

        foo = strstr(tmpCit, lookFor);
    } /* while */

    free(tmpCit);
    return(rtn);
} /* foundInALLTextXdOut */

/***************************************************************************
*
*  findLastWord --
*
*      This 
*
***************************************************************************/

char *findLastWord(char *lookAt)
{
    long i, startPos, pos, len;
    char *rtn;

    rtn = strdup(lookAt);
    len = (long)strlen(lookAt);
    startPos = len - 1;
    while((startPos > 0) && (lookAt[startPos] != ' '))
       startPos--;

    startPos++; /* Don't want the prefix blank */
    if(startPos > 0)
    {
        strcpy(rtn, "");
        pos = 0;
        for(i = startPos; i < len; i++)
          rtn[pos++] = lookAt[i];
        rtn[pos] = '\0';
    } /* fi */

    return(rtn);
} /* findLastWord */


/***************************************************************************
*
*  alsoHavePhenotype --
*
*      This 
*
***************************************************************************/

int alsoHavePhenotype(char *lcEntry)
{
   int rtn, done;
   long eLen;
   char *tmp, *tmp2, *tmp3, *eterm, *tmp5;

   /* February 16, 2017:

          "Isopod"-ab-1-"isopod"-adj-0
          "Indometacin"-ab-4-"indomethacin"-noun-0

      Trigger Information -- Comma separated sextuple showing what triggered MMI to identify this UMLS concept. Ordering tends to go from last to first found. The sextuple consists of
          "UMLS concept-loc-locPos-text-Part of Speech-Negation Flag".

      * UMLS Concept (Preferred or Synonym Text)

      * loc -- Location in the text if identifiable. ti - Title, ab - Abstract,
               and tx - Free Text

      * locPos -- Number of the utterance within the loc starting with one (1).
        For example, "ti-1" denotes the first utterance in the title.

      * text -- The actual text mapped to this UMLS concept identification.

      * Part of Speech -- Determined by the MedPost Tagger or Lexical Lookup 

      * Negation Flag -- 1 if the text is considered Negated by MetaMap and
           0 if it is not considered Negated.
*/

   eLen = (long)strlen(lcEntry);
   tmp = strdup(lcEntry);
   tmp2 = (char *)malloc(eLen + 1000);
   tmp3 = (char *)malloc(eLen + 1000);
   eterm = (char *)malloc(eLen + 1000);
   tmp5 = (char *)malloc(eLen + 1000);

   done = FALSE;
   while(!done && (strlen(tmp) > 0))
   {
      strcpy(tmp2, "");
      strcpy(tmp3, "");
      sscanf(tmp, "%[^:]:%[^\n]", tmp2, tmp3);

      while(!done && (strlen(tmp2) > 0))
      {
         strcpy(eterm, "");
         strcpy(tmp5, "");
         sscanf(tmp2, "%*[^-]-%*[^-]-%*[^-]-\"%[^\"]\"%*[^,],%[^\n]",
                eterm, tmp5);

         if(strlen(eterm) > 0)
         {
            if((strcmp(eterm, "phenotypes") == 0) ||
               (strcmp(eterm, "phenotype") == 0) ||
               (strcmp(eterm, "phenotyping") == 0))
            {
                rtn = TRUE;
                done = TRUE;
            } /* fi */
         } /* fi */

         strcpy(tmp2, tmp5);
      } /* while */

      strcpy(tmp, tmp3);
   } /* while */

   free(tmp);
   free(tmp2);
   free(tmp3);
   free(eterm);
   free(tmp5);

   return(rtn);
} /* alsoHavePhenotype */


/***************************************************************************
*
*  chgTexToolHost --
*
*      This 
*
***************************************************************************/

int chgTexToolHost()
{
   int rtn_random, nxt_random;
   long seedval;
   char line[251];
   FILE *fp;

   seedval = 0;
   if(strstr(default_TexToolHost, "ii-public") == NULL)
     strcpy(TexTool_Host, default_TexToolHost);
   else
     strcpy(TexTool_Host, hostname);

#ifdef RNDTTH_FILE
   fp = fopen(RNDTTH_FILE, "r+");
   if(fp != NULL)
   {
       fgets(line, 250, fp);
       rtn_random = -1;
       nxt_random = 0;
       sscanf(line, "%d", &rtn_random);

       if((rtn_random < 0) || (rtn_random >= numTTHosts))
         rtn_random = 0;

       nxt_random = rtn_random + 1;
       if(nxt_random >= numTTHosts)
         nxt_random = 0;

       rewind(fp);
       fprintf(fp, "%d\n", nxt_random);
       fclose(fp);
       strcpy(TexTool_Host, texToolHosts[rtn_random]);
   } /* fi */

   else
   {
       fprintf(stderr, "ERROR - Unable to open RND file: #%s#\n", RNDTTH_FILE);
       fflush(stderr);
   } /* else */
#else
   /* Randomize which TexTool we will call */

   seedval = time ((long int *) 0);
   srand48(seedval);
   rtn_random = GetNextRand();

   /* Verify the TexTool host before we go into the loop or assign default */

   if(numTTHosts > 1)
   {
       if((rtn_random > -1) && (rtn_random < numTTHosts))
         strcpy(TexTool_Host, texToolHosts[rtn_random]);
       else
         strcpy(TexTool_Host, default_TexToolHost);
   } /* fi */

   else
   {
       if(strstr(default_TexToolHost, "ii-public") == NULL)
         strcpy(TexTool_Host, default_TexToolHost);
       else
         strcpy(TexTool_Host, hostname);
   } /* else */
#endif
} /* chgTexToolHost */


