/***************************************************************************
*
*  foundInTextTI_asIs --
*
*      This 
*
***************************************************************************/

int foundInTextTI_asIs(char *lookFor)
{
   int rtn = FALSE;
   char *base, *foo;
   int okLeft, okRight, rOK;
   long i, j, baseLen, lfLen, diff, rPos, pos;

   okLeft = okRight = FALSE;
   baseLen = 0;
   lfLen = (long)strlen(lookFor);

   base = strdup(globalTitle);
   baseLen = (long)strlen(base);

   foo = strstr(base, lookFor);
   while(!rtn && (foo != NULL))
   {
       okLeft = okRight = FALSE;
       diff = foo - base;
       if(diff == 0)
          okLeft = TRUE;

       else if(diff > 0)
       {
          if(isspace(base[diff - 1]) || ispunct(base[diff - 1]))
            okLeft = TRUE;
       } /* else fi */

       if(okLeft)
       {
          pos = diff + lfLen;

          if((pos >= baseLen) || isspace(base[pos]) || ispunct(base[pos]))
          {
              rOK = TRUE;

              if(rOK)
                okRight = TRUE;
          } /* fi diff, space, or punct right */
       } /* fi okLeft */

       if(okLeft && okRight)
         rtn = TRUE;
 
       for(j = 0; j < lfLen; j++)
          base[j + diff] = 'X';

       foo = strstr(base, lookFor);
  } /* while foo */

   free(base);
   return(rtn);
} /* foundInTextTI_asIs */


