/***************************************************************************
*
*  removeEqualStmts --
*
*      We want to hide one token before and one token after an "="
#  So if we have "hello R != 0.008 goodbye" we want to end up with "hello xxxxxxxxxx goodbye"
#  if we have "hello r = -0.82 goodbye" we want to end up with "hello xxxxxxxxx goodbye"
#  if we have "hello SDNN = SD goodbye" we want to end up with "hello xxxxxxxxx goodbye"
*
***************************************************************************/

char *removeEqualStmts(char *text)
{
    int done, c, found, needSpaces;
    long i, len, diff, start, pos, lenDiff, endPos;
    char *rtn, *foo, term[SMALL_LINE + 1];

    len = (long)strlen(text);
    rtn = strdup(text);
    strcpy(term, "");

    foo = strchr(rtn, '=');
    if(foo != NULL)
    {
        while(foo != NULL)
        {
            diff = foo - rtn;
            done = FALSE;
            start = diff;

            needSpaces = 2;
            if(start > 0)
            {
                if((rtn[start - 1] == '!') || (rtn[start - 1] == '<') || (rtn[start - 1] == '>'))
                  start--;

                if(rtn[start - 1] != ' ')
                  needSpaces--;

                while((needSpaces > 0) && (start > 0))
                {
                    start--;
                    if(rtn[start] == ' ')
                      needSpaces--;
                } /* while */

                if(start < 0)
                  start = 0;

                if(rtn[start] == ' ')
                  start++;
            } /* fi */

            /* Now look for the end of the phrase */

            needSpaces = 2;
            endPos = diff + 1;
            if(endPos < len)
            {
                if((rtn[endPos + 1] == '='))
                  endPos++;

                if(rtn[endPos + 1] != ' ')
                  needSpaces--;
            } /* fi */

            while((needSpaces > 0) && (endPos < len))
            {
                endPos++;
                if(rtn[endPos] == ' ')
                  needSpaces--;
            } /* while */

            if(endPos > len)
              endPos = len;

            if(RTM_Debug)
            {
                fprintf(fout, "Masking Equation: \"");
                for(i = start; i < endPos; i++)
                  fprintf(fout, "%c", rtn[i]);
                fprintf(fout, "\n");
                fflush(fout);
            } /* fi */

            for(i = start; i < endPos; i++)
              rtn[i] = 'x';

            pos = 0;
            foo = strchr(rtn, '=');
        } /* while */
    } /* fi */

    return(rtn);
} /* removeEqualStmts */


