/***************************************************************************
*
*  addPT --
*
*      This 
*
***************************************************************************/

void addPT(char *DUI, char *loc, char *lookFor, char *MHname, char *globalET,
           char *globalPI, int flag, int isSpecial)
{
   char tmp[SMALL_LINE + 1], from[SMALL_LINE + 1], rest[SMALL_LINE + 1];
   int foundPT, nit, foundOther;
   long i, startPos, len;

   /* Make sure we don't add something we already have */

   foundPT = FALSE;
   for(i = 0; !foundPT && (i < numExistingPTs); i++)
   {
       if(MHname[0] == ExistPTs[i][0])
       {
           if(strcmp(MHname, ExistPTs[i]) == 0)
            foundPT = TRUE;
       } /* fi */
   } /* for */

   /* Now, make sure we don't add anything from the Full Text Only section of the Abstract */

   if(!foundPT && (strlen(globalPI) > 0) && haveFullText && (fullTextStartPos > 0))
   {
       foundOther = FALSE;
       strcpy(tmp, globalPI);
       while(!foundOther && (strlen(tmp) > 0))
       {
           strcpy(rest, "");
           startPos = len = nit = -1;
           sscanf(tmp, "%ld^%ld^%d;%[^\n]", &startPos, &len, &nit, rest);
           if((startPos > -1) && (len > -1) && (nit > -1))
           {
               if((startPos + len) < fullTextStartPos)
                 foundOther = TRUE;
           } /* fi */

           else
           {
               fprintf(stderr, "ERR: addPT globalPI Problem: (%s) tmp: #%s#\n", globalPI, tmp);
               fflush(stderr);
           } /* else */

           strcpy(tmp, rest);
       } /* while */

       if(!foundOther) /* Only from Full Text */
       {
           if(RTM_Debug)
           {
               fprintf(fout,
                "Ignoring PT (%s) - found only in Full Text - #%s# fullTextStartPos: %ld\n",
                MHname, globalPI, fullTextStartPos);
               fflush(fout);
           } /* fi */

           foundPT = TRUE;
       } /* fi */
   } /* fi */

   /* Make sure to not add Review if Systematic Review is provided by the Publisher. */

   if(!foundPT && (strcmp(MHname, "Review") == 0))
   {
       if(foundExistSysRev)
         foundPT = TRUE;  /* So we don't go ahead and add */
   } /* fi */

   /* For MTIR we are only allowing some of the Publication Types, so check here before 
      continuing if we are processing MTIR.  These are the onlyl PTs currently allowed
      for MTIR.  March 25, 2021.
   */

   if(!foundPT && MTI_REVIEW)
   {
       if((strcmp(MHname, "Case Reports") == 0) ||
          (strcmp(MHname, "Clinical Trial Protocol") == 0) ||
          (strcmp(MHname, "Clinical Trial, Phase I") == 0) ||
          (strcmp(MHname, "Clinical Trial, Phase II") == 0) ||
          (strcmp(MHname, "Clinical Trial, Phase III") == 0) ||
          (strcmp(MHname, "Clinical Trial, Phase IV") == 0) ||
          (strcmp(MHname, "Meta-Analysis") == 0) ||
          (strcmp(MHname, "Multicenter Study") == 0) ||
          (strcmp(MHname, "Observational Study") == 0) ||
          (strcmp(MHname, "Observational Study, Veterinary") == 0) ||
          (strcmp(MHname, "Randomized Controlled Trial") == 0) ||
          (strcmp(MHname, "Randomized Controlled Trial, Veterinary") == 0) ||
          (strcmp(MHname, "Review") == 0) ||
          (strcmp(MHname, "Systematic Review") == 0) ||
          (strcmp(MHname, "Twin Study") == 0) ||
          (strcmp(MHname, "Video-Audio Media") == 0))
        foundPT = FALSE;

       else
        foundPT = TRUE;
   } /* fi */

   if(!foundPT)
   {
       if(RTM_Debug)
       {
           fprintf(fout, "Adding Forced Publication Type: %s (%s)\n", MHname, lookFor);
           fflush(fout);
       } /* fi */

       if(strcmp(MHname, "Systematic Review") == 0)
         addedSysRevPT = TRUE;

       if(flag)
         sprintf(from, "Forced Pub Type Sentence Lookup:%s\0", lookFor);
       else
       {
           if(isSpecial)
             sprintf(from, "Forced Pub Type Lookup Special:%s\0", lookFor);
           else
             sprintf(from, "Forced Pub Type Lookup:%s\0", lookFor);
       } /* else */

       process_PTterm(MHname, loc, globalET, from, DUI, globalPI);

       /* If we have Case Report PT and not a Veterinary Journal, add Humans 
          per Susan's feedback January 6, 2021.
       */

       if(strcmp(MHname, "Case Reports") == 0)
       {
           if(!veterinaryJournal && !insectsJournal && !zootaxaJournal && !botanyJournal)
           {
               strcpy(from, "Forced Humans - Case Reports PT");
               process_mterm(FALSE, "", "Humans", MMI, 20000.0, MH, "", "", from, "D006801", "", "", TRUE);
           } /* fi */
       } /* fi */
   } /* fi */
} /* addPT */


/***************************************************************************
*
*  removeLowercaseGenus --
*
*      This
*
***************************************************************************/

char *removeLowercaseGenus(char *text)
{
    int done, c, ok;
    long i, x, len, diff, startPos, lfLen, endPos;
    char *rtn, *foo;

    len = (long)strlen(text);
    rtn = strdup(text);

    /* 
       "gen. nov., sp. nov."
       "gen. nov. sp. nov."
       "gen. nov. sp.nov."
       "gen. nov."
       "gen.nov."
       "sp. nov."
       "sp.nov."
    */

    lfLen = 0;
    foo = strstr(rtn, "gen. nov., sp. nov.");
    lfLen = 19;

    if(foo == NULL)
    {
        foo = strstr(rtn, "gen. nov. sp. nov.");
        lfLen = 18;
    } /* fi */

    if(foo == NULL)
    {
        foo = strstr(rtn, "gen. nov. sp.nov.");
        lfLen = 17;
    } /* fi */

    if(foo == NULL)
    {
        foo = strstr(rtn, "gen. nov.");
        lfLen = 9;
    } /* fi */

    if(foo == NULL)
    {
        foo = strstr(rtn, "nom. nov.");
        lfLen = 9;
    } /* fi */

    if(foo == NULL)
    {
        foo = strstr(rtn, "gen.nov.");
        lfLen = 8;
    } /* fi */

    if(foo == NULL)
    {
        foo = strstr(rtn, "nom.nov.");
        lfLen = 8;
    } /* fi */

    if(foo == NULL)
    {
        foo = strstr(rtn, "sp. nov.");
        lfLen = 7;
    } /* fi */

    if(foo == NULL)
    {
        foo = strstr(rtn, "sp.nov.");
        lfLen = 6;
    } /* fi */

    if(foo != NULL)
    {
        c = 0;
        while(foo != NULL)
        {
            diff = foo - rtn;
            endPos = diff + lfLen + 1;
            startPos = diff;
            done = FALSE;

            ok = TRUE;
            i = diff - 2; /* likely have "<space>sp. nov or similar so want char prior to space */

            if(RTM_Debug)
              fprintf(fout, "Lowercase Genuse/Species HEAD: #\n");
            while(!done && (i > 0))
            {
                if(rtn[i] == ' ')
                  done = TRUE;

                else if(!isalpha(rtn[i]) || !islower(rtn[i]))
                  ok = FALSE;

                if(!done)
                  startPos = i;

                if(RTM_Debug)
                  fprintf(fout, "%c - done: %d  ok: %d\n", rtn[i], done, ok);
                i--;
            } /* while */

            if(!ok)
              startPos = diff;

            if(RTM_Debug)
              fprintf(fout, "Lowercase Genuse/Species Removal (%ld): #", i);

            /* "x" out what we ended up finding */

            for(x = startPos; x < endPos; x++)
            {
                if(RTM_Debug)
                  fprintf(fout, "%c", rtn[x]);
                rtn[x] = 'x';
            } /* fi */

            if(RTM_Debug)
            {
               fprintf(fout, "#\n");
               fflush(fout);
            } /* fi */

            lfLen = 0;
            foo = strstr(rtn, "gen. nov., sp. nov.");
            lfLen = 19;

            if(foo == NULL)
            {
                foo = strstr(rtn, "gen. nov. sp. nov.");
                lfLen = 18;
            } /* fi */

            if(foo == NULL)
            {
                foo = strstr(rtn, "gen. nov. sp.nov.");
                lfLen = 17;
            } /* fi */

            if(foo == NULL)
            {
                foo = strstr(rtn, "gen. nov.");
                lfLen = 9;
            } /* fi */

            if(foo == NULL)
            {
                foo = strstr(rtn, "nom. nov.");
                lfLen = 9;
            } /* fi */

            if(foo == NULL)
            {
                foo = strstr(rtn, "gen.nov.");
                lfLen = 8;
            } /* fi */

            if(foo == NULL)
            {
                foo = strstr(rtn, "nom.nov.");
                lfLen = 8;
            } /* fi */

            if(foo == NULL)
            {
                foo = strstr(rtn, "sp. nov.");
                lfLen = 7;
            } /* fi */

            if(foo == NULL)
            {
                foo = strstr(rtn, "sp.nov.");
                lfLen = 6;
            } /* fi */
        } /* while */
    } /* fi */

    if(RTM_Debug)
    {
        fprintf(fout, "removeLowercaseGenus end Stage I: #%s#\n", rtn);
        fflush(fout);
    } /* fi */

    /* Now, look for things like "N. flavipalpis", single uppercase alpha followed by
       a period, then space, then all lowercase alpha word to the next space.
    */

    i = 0;
    while(i < len)
    {
        if((rtn[i] == ' ') && (i < (len - 5)))
        {
            if(isalpha(rtn[i + 1]) && isupper(rtn[i + 1]))
            {
                if((rtn[i + 2] == '.') && (rtn[i + 3] == ' '))
                {
                     if(isalpha(rtn[i + 4]) && islower(rtn[i + 4]))
                     {
                         ok = TRUE;
                         done = FALSE;
                         startPos = i + 4;
                         endPos = i + 4;
                         while(!done && (endPos < len))
                         {
                             if(rtn[endPos] == ' ')
                               done = TRUE;

                             else if(!isalpha(rtn[endPos]) || !islower(rtn[endPos]))
                                ok = FALSE;

                             if(!done)
                               endPos++;
                         } /* while */

                         if(ok)
                         {
                             if(RTM_Debug)
                               fprintf(fout,
                                  "Lowercase Genuse/Species singAlpha Removal (%ld): #", i);

                             /* "x" out what we ended up finding */

                             for(x = startPos; x < endPos; x++)
                             {
                                 if(RTM_Debug)
                                   fprintf(fout, "%c", rtn[x]);
                                 rtn[x] = 'x';
                             } /* fi */

                             if(RTM_Debug)
                             {
                                 fprintf(fout, "#\n");
                                 fflush(fout);
                             } /* fi */

                             i = (endPos - 1);
                         } /* fi ok */
                     } /* fi */
                } /* fi */
            } /* fi */
        } /* fi */

        i++;
    } /* while */

    if(RTM_Debug)
    {
        fprintf(fout, "removeLowercaseGenus end Stage II: #%s#\n", rtn);
        fflush(fout);
    } /* fi */
    return(rtn);
} /* removeLowercaseGenus */

